INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (9, 'هل سيخضع الأطفال وحديثو الولادة لفحص PCR أيضا؟', 'لا يتوجب على الرضع والأطفال دون 12 عاما إجراء فحص PCR، وما عدا ذلك فهو إلزامي.', 2, null, '2022-10-21 21:59:03', '2022-10-21 21:59:03');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (10, 'هل سيتم فرض الحجر الصحي على السياح؟', 'لا، فقط إذا ثبتت إصابتهم بكوفيد-19. يرجى الاطلاع على متطلبات شركة الطيران ومتطلبات السفر إلى دبي في ما يتعلق بفحوص PCR لفيروس كوفيد-19. يجب على المسافرين الذين يحتاجون إلى إجراء فحص "بي سي آر" عند الوصول إلى دبي الانتظار في مكان إقامتهم أو في فندق أو مبنى مخصّص للحجر الصحي من قبل السلطات حتى صدور النتائج في غضون 24 ساعة من إجراء الفحص. يُعفى المواطنون الإماراتيون والدبلوماسيون من هذه الشروط. يرجى الضغط هنا للاطلاع على لائحة بالبلدان التي تطبّق هذه الشروط.', 2, null, '2022-10-21 21:59:03', '2022-10-21 21:59:03');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (11, 'ماذا يحدث إذا ظهرت عليّ أعراض كورونا عند الوصول إلى مطار دبي وكانت نتيجة الفحص الذي أجريته إيجابية؟', 'يُفرض على جميع الزوار المصابين بـ "كوفيد- 19" حجر صحي لمدة 10 أيام. يلتزم الشخص المصاب بفيروس "كوفيد-19" المستجد بالحجر لمدة 10 أيام (في المنزل أو في الحجر المؤسسي). وتنتهي فترة حجر الشخص عندما يستكمل هذه المدة ويتحسّن وضعه ولا تظهر عليه أعراض الحمّى لمدة ثلاثة أيام متتالية على الأقل، من دون تناول أي دواء لتخفيفها. للمزيد من المعلومات، يُرجى زيارة موقع هيئة الصحة بدبي. ويُرجى من الأشخاص الذين خالطوا مصاباً بفيروس كوفيد-19 الاطلاع على هذا الرابط. الالتزام التام بالإجراءات الوقائية وقضاء فترة حجر صحي في فنادق مخصصة أو في المنزل أو في مستشفى بحسب الوضع الصحي للمُصاب ومتطلبات العزل. ويتم خلال فترة الـ 10 أيام اتباع كافة الإجراءات الضرورية ومراقبة أي أعراض.', 2, null, '2022-10-21 21:59:03', '2022-10-21 21:59:03');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (12, 'هل يتوجب على مسافري الترانزيت اتباع ذات الإجراءات المتعلقة بفحص PCR والتوقيع على الإقرار الطبي، إلخ؟', 'سيكون على مسافري الترانزيت إجراء الفحص المخبري لفيروس كورونا "بي سي آر" المسبق فقط للقادمين من بعض الوجهات المعينة أو وفقاً لمتطلبات وجهة السفر النهائية. يرجى الإطلاع على التفاصيل عبر هذا الرابط إذا كانت نتيجة الفحص سلبية يتوجب على المسافر طباعتها باللغة العربية أو الإنكليزية. تُقبل نتائج "بي سي آر" بلغات أخرى إذا كان من الممكن التحقق منها في المحطة الأصلية. لا تُقبل أية نتائج مكتوبة بخط اليد أو عبر رسائل نصية أو رقمية. يجب على المسافرين التحقق من إرشادات السفر الخاصة ببلدهم للحصول على أحدث الإجراءات المتعلقة برحلات المغادرة، بالإضافة إلى متطلبات شركات الطيران.', 2, null, '2022-10-21 21:59:03', '2022-10-21 21:59:03');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (13, 'ما هي الإجراءات المتّبعة عند مغادرة دبي؟', 'يترتّب على المسافرين الذين يغادرون مطارات دبي إجراء فحص "بي سي آر" فقط في حال فرضه البلد الذي يسافرون إليه. سيتوفر فحص "بي سي آر" سريع أو اختبار "المستضد السريع" في المطار للمسافرين المغادرين من دبي إذا فرضه البلد الذي يقصدونه. يُنصح المسافرون بالاطلاع على بروتوكولات السفر الخاصة بشركة الطيران والبلد الذي يقصدونه. ملاحظة:  بإمكان شركات الطيران رفض أي مسافر تظهر عليه أعراض "كوفيد-19".', 2, null, '2022-10-21 21:59:03', '2022-10-21 21:59:03');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (14, 'ما هي القواعد التي يجب اتّباعها خلال إقامتي في دبي؟', 'مسؤوليات السياح: التقيّد بكافة الإجراءات والتعليمات الرسمية الصادرة عن حكومة دبي والدول الأخرى، قبل السفر وبعد العودة إلى دبي.  الإفصاح عن أي أعراض للإصابة بـ "كوفيد- 19 " قبل السفر بملء نموذج الإقرار الطبي الذي توفره شركات الطيران. يتحمّل السياح تكاليف الفحوصات والعلاج والحجر في حالة إصابتهم بـ "كوفيد- 19". ولهذا فإنه من المهم أن يكون لديهم تأمين سفر دولي يغطي الإصابة بالفيروس قبل السفر. اتخاذ الإجراءات الوقائية ومراقبة أي أعراض للفيروس. اتباع إرشادات العزل في حالة ظهور أعراض للمرض عند الوصول وكانت النتيجة إيجابية.', 2, null, '2022-10-21 21:59:03', '2022-10-21 21:59:03');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (15, 'هل يتوجّب عليّ وضع كمامة خلال إجازتي في دبي؟', 'يُعتبر ارتداء الكمامة إلزامياً. عليك وضع الكمامة في جميع الأوقات، إلا إذا كنت تمارس الرياضة أو تأكل أو تشرب أو تتواجد في السيارة مع عائلتك أو تقود السيارة بمفردك بالإضافة إلى ذلك، يتعيّن عليك الالتزام بقواعد التباعد الاجتماعي  ', 2, null, '2022-10-21 21:59:03', '2022-10-21 21:59:03');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (16, 'هل يجب على السياح دفع رسوم العلاج والإقامة في الفندق خلال فترة الحجر الصحي إذا ظهرت عليهم أعراض فيروس كورونا أو كانت نتائج تحاليلهم إيجابية؟', 'يتحمّل المسافر تكاليف العلاج والحجر. ابتداءً من 1 ديسمبر 2020، بات بإمكان العملاء الذين يشترون تذكرة على متن طيران الإمارات أو فلاي دبي الاستفادة من تأمين إضافي على السفر من شركة "إيه آي جي ترافل" أو "نكست كير" حتى 31 مارس 2021، يغطي مخاطر متعددة تشمل كوفيد-19 والنفقات الطبية في الخارج. يرجى الاطلاع على الروابط التالية للمزيد من المعلومات والأجوبة على الأسئلة المتكررة: طيران الإمارات | فلاي دبي تشمل خيارات الحجر الصحي الإقامة في فنادق محددة أو في المنزل أو في مستشفى بحسب الوضع الصحي للمريض ومتطلبات العزل. يلتزم الشخص المصاب بفيروس "كوفيد-19" بالحجر لمدة 10 أيام (في المنزل أو في الحجر المؤسسي). وتنتهي فترة حجر الشخص عندما يستكمل هذه المدة ويتحسّن وضعه ولا تظهر عليه أعراض الحمّى لمدة ثلاثة أيام متتالية على الأقل، من دون تناول أي دواء لتخفيفها. للمزيد من المعلومات، يُرجى زيارة صفحة هيئة الصحة بدبي. ويُرجى من الأشخاص الذين خالطوا مصاباً بفيروس كوفيد-19 الاطلاع على هذا الرابط. يتحمّل السياح تكاليف العلاج والحجر، إلا في حال قدومهم على متن إحدى رحلات طيران الإمارات أو فلاي دبي.', 2, null, '2022-10-21 21:59:03', '2022-10-21 21:59:03');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (17, 'إذا كان السياح يقيمون عند الأقارب والعائلات، أو الأصدقاء، وظهرت عليهم أعراض "كوفيد-19" عند الوصول وطُلب منهم إجراء فحص آخر، فهل يلزمهم الحجر الصحي؟', 'إذا كانت نتيجة فحص "كوفيد-19" إيجابية ، فيجب على المسافر الالتزام بإرشادات العزل. سيتم تحديد مكان العزل وفقاً لحالة الأسرة والحالة الصحية للمصاب (عزل مؤسسي أو في المستشفى). يلتزم الشخص المصاب بفيروس كوفيد-19 المستجد بالحجر (في المنزل أو في الحجر المؤسسي). تمتد فترة حجر الشخص الذي تأكّدت إصابته بفيروس كوفيد-19 على 10 أيام، وتنتهي عندما يستكمل هذه المدة ويتحسّن وضعه ولا تظهر عليه أعرض الحمّى لمدة 3 أيام متتالية على الأقل، من دون تناول أي دواء لتخفيفها. للمزيد من المعلومات، يُرجى زيارة صفحة هيئة الصحة بدبي. ويُرجى من الأشخاص الذين خالطوا مصاباً بفيروس كوفيد-19 الاطلاع على هذا الرابط.', 2, null, '2022-10-21 21:59:03', '2022-10-21 21:59:03');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (18, 'هل أحتاج إلى تأشيرة؟ وأين أتقدم بطلب الحصول عليها؟', 'للاطلاع على كافة المعلومات المرتبطة بتأشيرة الزيارة، يرجى مراجعة موقع الإدارة العامة للإقامة وشؤون الأجانب بدبي وكذلك الاطلاع على أحدث الإرشادات التوجيهية الخاصة بالسفر والصادرة في بلدك.', 2, null, '2022-10-21 21:59:03', '2022-10-21 21:59:03');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (19, 'هل سيعاود طيران الإمارات تشغيل رحلاته بشكل كامل؟', 'يرجى الاطلاع على المعلومات الواردة في هذا الرابط لمعرفة الوجهات التي تُسيّر طيران الإمارات الرحلات إليها أو منها.', 2, null, '2022-10-21 21:59:03', '2022-10-21 21:59:03');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (20, 'هل تم استئناف عقد الاجتماعات والمؤتمرات والمعارض؟', 'نعم، تم استئناف الاجتماعات والمؤتمرات والمعارض المحلية والدولية، إضافة إلى الفعاليات الترفيهية وفعاليات قطاع التجزئة. تلتزم جميع الفعاليات في دبي بالإجراءات الوقائية والاحترازية. يمكن أن تصل سعة فعاليات الأعمال إلى 70% مع الالتزام بجميع بروتوكولات الصحة والسلامة التي تصدرها الجهات المعنيّة. كما يمكن أن يصل عدد المشاركين في فعاليات الشركات واحتفالات توزيع الجوائز إلى 1000 شخص كحدّ أقصى من دون إبراز شهادة التلقيح.', 2, null, '2022-10-21 21:59:03', '2022-10-21 21:59:03');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (21, 'هل تمّ استئناف الفعاليات في دبي؟', 'يمكن تنظيم الأنشطة الترفيهية والفعاليات المسرحية والكوميدية وغيرها شرط الحصول على تصريح للقيام بذلك مع التزام منظّمي الفعالية والمشاركين بأحدث التعليمات وتدابير السلامة والوقاية. لمزيد من المعلومات، يرجى التواصل مع مركز الاتصال التابع لدائرة الاقتصاد والسياحة بدبي على الرقم التالي: 559 555 600. يجب الالتزام بسعة استيعاب تبلغ 70% عند إقامة الحفلات أو الفعاليات الترفيهية والرياضية والتجارية (بما في ذلك الحضور)، مع التقيّد بتعليمات التباعد الاجتماعي. يمكن أن تصل القدرة الاستيعابية في الأنشطة والفعاليات الاجتماعية إلى 60% شرط ألاّ تتخطى 300 شخص. يرجى الاطلاع على أحدث التعليمات والبروتوكولات المنشورة لمعرفة المتطلبات الخاصة بالتلقيح في الفعاليات.  ', 2, null, '2022-10-21 21:59:03', '2022-10-21 21:59:03');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (22, 'هل يمكن إقامة حفلات الزفاف في دبي؟', 'نعم، يمكن التخطيط لإقامة حفلات الزفاف واستضافتها في دبي بقدرة استيعابية تصل إلى 60% شرط ألاّ يتخطى عدد المشاركين 300 شخص. يُنصح كبار السن والأشخاص الذين يعانون من أمراض مزمنة بعدم المشاركة في اللقاءات والفعاليات.', 2, null, '2022-10-21 21:59:03', '2022-10-21 21:59:03');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (23, 'متى يُقام معرض إكسبو 2020 وأين يمكنني الحصول على مزيد من المعلومات؟', 'تُقام هذه الفعالية العالمية بين 1 أكتوبر و31 مارس 2022. للاطلاع على آخر المستجدات يرجى زيارة الموقع https://www.expo2020dubai.com/ أو متابعة Expo2020dubai@ على وسائل التواصل الاجتماعي.', 2, null, '2022-10-21 21:59:03', '2022-10-21 21:59:03');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (24, 'هل يجب أن أكون قد تلقيت اللقاح لزيارة إكسبو 2020؟', 'يجب على جميع الزوّار إبراز وثيقة تثبت تلقّيهم جرعة واحدة على الأقلّ من أي لقاح كوفيد-19 معترف به من قبل أي دولة مشاركة في إكسبو 2020 أو من قبل منظمة الصحة العالمية. إذا لم يتلقَّ الزوّار اللقاح، يجب عليهم إبراز شهادة فحص بي سي آر بنتيجة سلبية تمّ إجراؤه في غضون الـ72 ساعة الماضية. للحصول على مزيدٍ من المعلومات حول تدابير السلامة المتّبعة في إكسبو 2020 لمكافحة جائحة كوفيد-19، يرجى الضغط هنا.', 2, null, '2022-10-21 21:59:03', '2022-10-21 21:59:03');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (25, 'أين يمكنني معرفة الإجراءات والمتطلبات الرسمية الصادرة عن حكومة دبي؟', 'يجب على الجميع اتباع كافة الإجراءات الوقائية والتي تشمل: الحفاظ على مسافة آمنة بينهم وبين الآخرين وفقاً لتعليمات التباعد الاجتماعي. وضع الكمامات. في ما يلي الروابط الخاصة بالمصادر الرسمية: مطارات دبيالموقع الرسمي بلدية دبي الموقع الرسمي هيئة الصحة بدبي الموقع الرسمي الهيئة الاتحادية للهوية والجنسية الموقع الرسمي الإدارة العامة للإقامة وشؤون الأجانب - دبي الموقع الرسمي وزارة الصحة ووقاية المجتمع - الإمارات العربيه المتحدة الموقع الرسمي', 2, null, '2022-10-21 21:59:03', '2022-10-21 21:59:03');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (26, 'متى أكون بحاجة إلى تصريح لإقامة فعالية أو نشاط؟', 'الجهات التالية بحاجة إلى تصريح لإقامة نشاط: الفنادق ومراكز التسوق والمطاعم أو أي وجهة تنظم فعاليات ترفيهيّة بشكل متكرر مثل الحفلات الموسيقية أو العروض أو أنشطة الأطفال في منطقة ألعاب مؤقتة.', 2, null, '2022-10-21 21:59:03', '2022-10-21 21:59:03');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (27, 'ماذا يحصل في حال تمّ رفض تصريحي لإقامة نشاط؟', 'يمكنك مناقشة حالتك من خلال التواصل مع قسم التصاريح الإلكترونية في دائرة الاقتصاد والسياحة بدبي عن طريق البوابة الإلكترونية. سيتم الرد على التساؤلات والنظر في كلّ حالة على حِدة.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (28, 'متى سأحصل على تصريح إقامة النشاط بعد الموافقة عليه؟', 'عند الموافقة، سوف يتمّ إصدار تصريحك في غصون يوم عمل واحد.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (29, 'ما هي المدة التي يمكن أن يستمرّ خلالها النشاط؟', 'يمكن أن تستمرّ الأنشطة من يومٍ واحد إلى ثلاثة أشهر.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (30, 'من يمكنه التقدم بطلب للحصول على هذه الخدمة؟', 'يُمكن للفنادق فقط التقدم بطلب للحصول على تصريح بتركيب خيمة مؤقتة. أما بالنسبة للمواقع الأخرى، فيجب تقديم الطلبات إلى دائرة التنمية الاقتصادية.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (31, 'متى يمكنني التقدم بطلب الحصول على تصريح بتركيب خيمة مؤقتة؟', 'يمكنك التقدم بطلب الحصول على تصريح بتركيب خيمة مؤقتة قبل موعد إقامة الفعالية بـ 24 ساعة.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (32, 'هل يمكنني التقدم بطلب الحصول على تصريح لإقامة فعالية ترفيهية في خيمة ترفيهية؟', 'نعم، حالما يتمّ إصدار تصريح بتركيب خيمة ترفيهية، سوف تتمّ إضافة الخيمة إلى قائمة المواقع الفرعية التابعة للفندق. يمكنك التقدُّم بطلب للحصول على تصريح لإقامة الفعالية الترفيهية عن طريق اختياره من هذه القائمة.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (33, 'هل يمكنني أن أعدّل طلبي للحصول على تصريح لتركيب خيمة ترفيهية؟', 'لا، ولكن يمكنك التقدُّم بطلب للحصول على تصريح جديد.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (34, 'هل يمكنني تمديد طلبي للحصول على تصريح بتركيب خمية ترفيهية؟', 'يمكنك التقدُّم بطلب الحصول على تصريح جديد وإدراج التواريخ الجديدة الخاصة بك.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (35, 'هل يمكنني إضافة نوع جديد من الأنشطة إلى تصريح تم إصداره سابقاً؟', 'نعم، هذا ممكن لتصاريح الأنشطة الترفيهية.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (36, 'كم مرة يمكنني تعديل تصريح إقامة النشاطات؟', 'يمكنك إجراء تعديلات بقدر ما تشاء. يجب إجراء بعض التعديلات قبل بدء النشاط فيما يمكن القيام بتعديلات أخرى عندما يكون النشاط لا يزال قائماً.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (37, 'هل تتطلب هذه الخدمة دفع أي رسوم؟', 'لا، إنها خدمة مجانية.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (38, 'ما هي أنواع الأنشطة الخاصة؟', 'فعاليات تتّسم بطابع شخصي مثل أعياد الميلاد وحفلات الخطبة والزفاف. وتندرج بعض فعاليات الشركات ضمن الأنشطة الخاصة مثل اجتماعات الموظفين (طالما أنّ الحضور من الموظفين العاملين في المؤسسة نفسها).', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (39, 'ما هي المدة التي تستغرقها الموافقة على تعديل النشاط؟', 'بمجرّد الموافقة، سيتمّ تعديل تصريحك في غضون يوم عملٍ واحد.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (40, 'متى يمكنني التقدم بطلب لإقامة نشاط خاص؟', 'بمجرّد أن تقوم بحجز الموقع الخاص بالفعالية، يمكنك التقدم بطلب للحصول على تصريح لإقامة نشاطٍ خاص. يمكن تلقّي الطلبات حتى 24 ساعة قبل بدء الفعالية.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (41, 'ما الذي يميّز كلية دبي للسياحة؟', 'تتمتّع كلية دبي للسياحة بدعم حكومي وقد أسّستها دائرة الاقتصاد والسياحة بدبي. يشمل المنهاج الدراسي للكلية المهارات الحياتية وآليات بناء الثقة التي يحتاج إليها الخرّيجون للعمل في قطاع السياحة. توفر الكلية ذلك من خلال منصةٍ للتدريب العملي والمهني، بما في ذلك التدريب الداخلي والزيارات الميدانية وورش العمل والمشاريع، التي يقوم بتدريسها أساتذة يمتلكون خبرة مباشرة في هذا المجال. كما تستضيف الكلية عدداً من أفضل الروّاد المحليين والعالميين في قطاع السياحة ليستفيد الطلاب من خبراتهم الطويلة في هذا المجال.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (42, 'هل المواد الدراسية التي تقدّمها كلية دبي للسياحة مُعتمدة من جهة معيّنة؟', 'نعم. تعتمد هيئة المعرفة والتنمية البشرية في دبي موادنا الدراسية، كما حصلت كلية دبي للسياحة أيضاً على اعتماد عالمي من معهد Institute of Hospitality لبرامجها في مجال السياحة والفعاليات والضيافة وفنون الطهي.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (43, 'هل تلزمني شهادة IELTS أو TOEFL لتقديم الطلب؟', 'يتعيّن على الطلاب الأجانب أن يحصلوا على شهادة IELT أو TOEFL بينما ينبغي على الطلاب المقيمين في دولة الإمارات اجتياز اختبار تحديد مستوى اللغة الإنجليزية.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (44, 'متى يُمكنني الالتحاق بالكليّة؟', 'يتمّ قبول الطلبات لبدء الدراسة في شهر سبتمبر. يرجى تقديم الطلب قبل حلول ذلك الموعد.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (45, 'هل يمكنني زيارة الكلية؟', 'نعم. نفتح أبوابنا لاستقبال الزوار في أيام محددة، لكن يمكنك تقديم طلب لزيارتنا في أي وقت. نحن نتطلع للترحيب بك ومرافقتك في جولة داخل الكلية. لتحديد موعد الزيارة، يمكنك التواصل معنا عبر البريد الإلكتروني info@dct.ac.ae.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (46, 'ما هي تكلفة الدراسة؟', 'يمكن الاطلاع على أحدث لائحة للأسعار على الموقع الإلكتروني التابع لكلية دبي للسياحة.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (47, 'هل يمكنني دفع التكاليف بالتقسيط؟', 'يتمتع المقيمون في دولة الإمارات العربية المتحدة بخيار تسديد الرسوم والتكاليف على دفعات.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (48, 'هل يجب عليّ الدفع لتقديم طلب؟', 'لا، الطلبات مجانية.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (49, 'ما هي الشروط المطلوبة للتقدُّم بطلب الحصول على تصريح لإقامة مخيم صحراوي؟', 'للتقدُّم بطلب الحصول على تصريح لإقامة مخيم صحراوي، يجب أن تستوفي شركتك الشروط التالية: تصريح رحلات سفاري ساري المفعول يشمل 10 مركبات مملوكة ومسجلة بإسم الشركة، أو خمس مركبات بالإضافة إلى حافلة مسجلة بإسم الشركة تتسع لما لا يقل عن 45 مقعداً. ما لا يقل عن خمسة مرشدين سياحين يعملون في الشركة. موافقة من الإدارة العامة للدفاع المدني. موافقة من بلدية دبي.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (50, 'هل يمكنني التقدُّم بطلب للحصول على تصريح لإقامة مخيم صحراوي مع سائقين أو مرشدين سياحين مستقلين؟', 'لا، يجب أن يكون سائقو رحلات السفاري والمرشدون السياحيون مشمولين ضمن تأشيرة الشركة.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (51, 'ما هي مدة صلاحية تصريح إقامة المخيم الصحراوي؟', 'تمتدّ صلاحية تصريح إقامة المخيم الصحراوي لمدة عامٍ واحد أو حتى تاريخ انتهاء عقد الإيجار.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (52, 'كيف يمكنني تجديد تصريح المخيم الصحراوي الخاص بي؟', 'عند انتهاء صلاحية التصريح الخاص بك، ينبغي عليك تقديم طلب للحصول على تصريح جديد.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (53, 'هل يمكنني تعديل تصريح إقامة المخيم الصحراوي لزيادة مساحته؟', 'نعم، بإمكانك فعل ذلك طالما تملك جميع الوثائق اللازمة بحيث تكون سارية المفعول.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (54, 'كم عدد المرشدين السياحيين الذين يمكنني إضافتهم إلى تصريحي؟', 'يجب أن يكون لدى شركتك ما لا يقل عن خمسة مرشدين سياحيين لإصدار تصريح. يمكنك إضافة أي عدد ترغب به من المرشدين السياحيين شرط ألا يقل عن خمسة مرشدين.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (55, 'كيف يمكنني تعديل أو إضافة مركبة جديدة إلى تصريح إقامة المخيم الصحراوي؟', 'عند تعديل تصريحك، بإمكانك تغيير هذه التفاصيل.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (56, 'من يستطيع التقدم بطلب للحصول على تصريح مكتب تنظيم جولات سياحية؟', 'منظمو الجولات السياحية والشركات السياحية.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (57, 'كم يستغرق التقدم بطلب والحصول على موافقة لإنشاء مكتب أو كشك لتنظيم الجولات السياحية؟', 'قد يستغرق الحصول على موافقة لإنشاء مكتبٍ أو كشك سياحي مدةً تصل إلى 24 ساعة.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (58, 'هل يمكنني تعديل تصريح المكتب أو تجديده؟', 'لا، يتعيّن عليك التقدم بطلب الحصول على تصريح جديد بعد انتهاء صلاحيته.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (59, 'هل هناك مواصفات مطلوبة للصور الخاصّة بمكتب السفر؟', 'نعم، يجب أن تكون الصور واضحة وتُظهر المكتب من زاويتين مختلفتين.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (60, 'ما هو ختم "دبي الضمانة" ؟', 'هي مبادرة جديدة للتأكد من امتثال المنشآت السياحية والتجارية بما فيها الفنادق ومحلات تجارة التجزئة والمطاعم والوجهات السياحية بجميع بروتوكولات الصحة العامة للوقاية من فيروس كورونا المستجد "كوفيد-19" وحسن إدارة هذه الأزمة، وذلك كجزء من الجهود المبذولة من أجل ضمان جاهزية دبي لاستقبال زوارها ابتداء من 7 يوليو الجاري. وتساهم هذه المبادرة في طمأنة الضيوف بأن هناك إلتزام من قبل الجميع بإجراءات السلامة والصحة العامة التي تفرضها السلطات المختصة في كل مرحلة من مراحل رحلة الزوار .', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (61, 'ما أهمية "دبي الضمانة"؟', 'في الوقت الذي أصبحت فيه الصحة والإجراءات الوقائية المتبعة للحفاظ على الصحة العامة على رأس اهتمامات المسافرين عند اختيار وجهتهم المفضلة للسفر، فإن ختم "دبي الضمانة" يعتبر من المبادرات المهمة التي ستساهم في طمأنة الزوار، الذين سيخوضون تجارب ممتعة وأيضا آمنة في كل مرحلة من مراحل رحلتهم بدءاً من وصولهم إلى دبي وحتى مغادرتهم لها.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (62, 'ما هي الجهة التي أطلقت "دبي الضمانة"؟', 'يُعتبر ختم "دبي الضمانة" ثمرة تعاونٍ بين دائرة الاقتصاد والسياحة بدبي وبلدية دبي.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (63, 'ما هي المنشآت التي يمكنها المشاركة؟', 'بإمكان كافة الفنادق ومناطق الجذب السياحية ومراكز التسوق ومحلات التجزئة والمطاعم والمقاهي ومراكز الترفيه المشاركة  في هذه المبادرة والالتزام بكافة الإجراءات الوقائية لمكافحة فيروس كورونا المستجد "كوفيد-19"، حيث ستساهم هذه المبادرة بإظهار مدى التزام المنشأة بإرشادات الصحة  والسلامة، ما من شأنه أن يبعث روح الطمأنينة في نفوس الضيوف، وكذلك يعزز من تجاربهم الآمنة.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (64, 'كيف يمكن لمنشأة أن تصبح معتمدة من قِبل "دبي الضمانة"؟', 'يجب أن تمتثل المنشآت امتثالاً تاماً لإرشادات الصحة والسلامة الصادرة عن دائرة الاقتصاد والسياحة بدبي. تصدر الشهادة بعد جولات التفتيش الدورية كل 15 يوماً، وسيتم تزويد المنشأة بدليل إرشادي إلكتروني يحمل شعار "دبي الضمانة" مع إرشادات خاصة بالعلامة التجارية. بعد ذلك، يمكن للمنشأة عرض الشهادة في أماكن العمل.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (65, 'ما هي مدة صلاحية ختم "دبي الضمانة" بعد كل تفتيش؟', 'سيتم إعطاء ختم "دبي الضمانة" مجاناً وسيكون صالحاً لمدة 15 يوماً، فيما سيتم تجديده كل أسبوعين بعد جولات تفقدية من قبل موظفي الجهات المختصة على تلك المنشآت.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (66, 'ما هي تدابير الصحة والسلامة التي يجب أن تستوفيها منشأة ما للحصول على ختم "دبي الضمانة"؟', 'يمكن الحصول على إرشادات مفصّلة خاصّة بكل قطاع من دائرة الاقتصاد والسياحة بدبي. تمّ اتخاذ تدابير محدّدة إلى جانب التدابير الاحترازية الأساسية المتمثلة في الحفاظ على المسافة الآمنة بين الضيوف والموظفين ووضع الكمامات في جميع الأوقات. طبقت الفنادق في دبي أيضاً نظام تعقيم صارم ومُحسّن، حيث قامت بتعقيم المناطق التي يكثر فيها التلامس مثل الردهات والمطاعم وأحواض السباحة وغيرها. كما تمّ تجهيز مداخل الفنادق بمعداتٍ لقياس درجة حرارة الضيوف. تستخدم مراكز التسوق تقنيات متقدمة للحدّ من انتشار الجائحة، بما في ذلك الماسحات الضوئية والكاميرات الحرارية لمراقبة حركة الزوّار في جميع النقاط. أُعيد فتح المنتزهات العامة والشواطئ التابعة لبلدية دبي مع ضرورة التقيد بإرشادات السلامة الصارمة. يُسمح للمطاعم بتقديم خدماتها للضيوف واستقبال عدد محدود منهم، مع الحفاظ على مسافة مترين بين الطاولات.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (67, 'هل هناك موقع إلكتروني لمبادرة "دبي الضمانة" ؟', 'نعم، يرجى زيارة الموقع www.DubaiAssured.ae لمعرفة المنشآت الحاصلة على ختم "دبي الضمانة". ', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (68, 'هل هناك فعاليات لا يمكن إدراجها في جدول فعاليات دبي؟', 'لا يمكن إدراج أنواع الفعاليات التالية في جدول فعاليات دبي: الفعاليات الخاصة أو التي يمكن الدخول إليها بدعوة خاصة فقط فعاليات إطلاق المنتجات التابعة لعلامة تجارية ما أو العروض الترويجية داخل المتاجر الفعاليات التي تُباع خلالها الأعمال الفنية أو السلع بالتجزئة أو المنتجات التجارية أي تجارب أداء أو ورش عمل أو دورات أو فصول دراسية الفعاليات أو البرامج أو المخيّمات المدرسية الفردية الفعاليات التي تخدم المنفعة الشخصية، مثل حفلات الأعراس، والتخرّج، وأعياد الميلاد، وغيرها الفعاليات التي تشتمل على برامج لتجمعات سياسيّة هدفها الرئيسيالتأثير على التشريعات في الدولة الفعاليات التي تروّج للمشروبات والأسلحة والمواد الإباحية والقِمار وسحوبات اليانصيب والقضايا السياسية أو التنافسية', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (69, 'هل يجب أن أمتلك تصريحاً لبيع التذاكر الإلكترونية من أجل إدراج فعاليتي على جدول فعاليات دبي؟', 'ليس من الضروري أن تمتلك تصريحاً لبيع التذاكر الإلكترونية من أجل إدراج فعاليتك على جدول فعاليات دبي. ولكن، يجب عليك امتلاك تصريح لإقامة الفعالية.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (70, 'هل يمكنني تعديل أو إزالة إدراج الفعالية الخاصة بي في جدول فعاليات دبي؟', 'إذا أردت تغيير إدراج الفعالية الخاصة بك، يرجى التواصل معنا.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (71, 'كم من الوقت يستغرق ظهور الفعالية الخاصة بي على جدول فعاليات دبي؟', 'سوف يتمّ إدراج الفعالية الخاصة بك في غضون يومَي عمل إذا كانت جميع وثائقك كاملة.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (72, 'هل يمكنني الحصول على الدعم المالي للفعالية الخاصة بي؟', 'نعم. توفر دائرة الاقتصاد والسياحة بدبي فرصاً للرعاية. اقرأ المزيد عن خيارات الرعاية', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (73, 'أين يمكنني التسجيل للمشاركة في التدريب مع "خبير دبي"؟', 'يرجى زيارة www.dubaiexpert.ae  ', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (74, 'من يُعتبر مؤهلاً ليصبح "خبير دبي"؟', 'يُعتبر وكلاء السفر ومنظّمو الجولات السياحية فقط مؤهلين ليكونوا خبراء دبي.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (75, 'ما الذي أحتاجه لأكون "خبير دبي"؟', 'لتكون "خبير دبي"، يُطلب منك: — الحصول على علامات عالية في 17 مهمّة على الأقل — مشاهدة جميع مقاطع الفيديو الـ 25 المدمجة في اللعبة', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (76, 'ما هي المدة المتوقّعة لاستكمال تدريب "خبير دبي"؟', 'نوصي بتمديد فترة تدريبك في برنامج "خبير دبي" لبضعة أيام لتكتسب معلومات وافية وتصبح أكثر معرفة بكل ما يتعلّق بالمدينة.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (77, 'ما هي المدة المطلوبة لأكون "خبير دبي"؟', 'لكي تصبح "خبير دبي"، يتطلب الأمر إكمال 17 مهمّة ومشاهدة 25 مقطع فيديو والإجابة على 50 سؤالاً خلال وقت زمني محدّد. يمكن إتمام ذلك في يومٍ واحد، ولكن ننصحك بأن تواصل اكتشاف البرنامج لفترة أطول.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (78, 'ما هي المستويات التي يمكنني تحقيقها في برنامج "خبير دبي"؟', 'يمكنك أن تحقّق إحدى هذه النتائج الثلاثة لكل مهمّة تقوم بها. أداء مثالي: استطاع اللاعب تحقيق نتيجة عالية بما يكفي لفتح جميع المكافآت الثلاثة المدمجة في المهمّة. إتمام المهمة: أنجز اللاعب مهمّةً بنجاح ولكنه لم يتمكن من تحقيق نتيجة عالية بما يكفي. فشل المهمّة: تفشل المهمّة عندما ينخفض مستوى رضا المسافر إلى صفر.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (79, 'هل يتوفر مستوى إضافي يجعلني أتقدم أكثر في برنامج خبير دبي؟', 'نعم. يمكنك أن تكون "خبير دبي بلاس" عن طريق استيفاء المعايير التالية: - نتائج مثالية في 27 مهمّة - مشاهدة جميع مقاطع الفيديو الـ25 - الإجابة على جميع الأسئلة الـ55 في الوقت المحدد وبشكلٍ صحيح', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (80, 'ما هي المتطلبات الفنية لممارسة لعبة "خبير دبي"؟', 'يعمل برنامج "خبير دبي" بشكلٍ أفضل على متصفّحَي "كروم" و"فايرفوكس". سيستغرق تنزيل لعبة "خبير دبي" من دقيقتين إلى خمس دقائق ويجب أن يبقى المتصفّح مفتوحاً خلال هذه المدة. سيؤدي الانتقال إلى صفحة أخرى إلى إيقاف تنزيل اللعبة مؤقتاً، وقد يؤدي استخدام شبكة خاصة افتراضية VPN إلى تعطيل عمل برنامج "خبير دبي".', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (81, 'هل يعمل برنامج "خبير دبي" على جميع متصفّحات الويب؟', 'للحصول على الأداء الأمثل، نقترح أن تُشغّل لعبة "خبير دبي" على متصفّحَي "كروم" أو "فايرفوكس".', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (82, 'هل يتوفر تطبيق خاص ببرنامج "خبير دبي"؟', 'نعم. يتوفر تطبيق "خبير دبي" على متجر تطبيقات "آبل" ومتجر "غوغل بلاي".', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (83, 'ما هو برنامج "نهج دبي"؟', 'يُعتبر برنامج "نهج دبي" منصةً لتدريب وصقل مهارات أي شخص يتفاعل مع السيَّاح في دبي ضمن إطار عمله. يُعدّ امتلاك المعارف والمعلومات الصحيحة حول دبي وتاريخها وثقافتها وتراثها والتمتّع بمهاراتٍ ممتازة في خدمة العملاء، أمراً مهماً جداً لتوفير تجارب استثنائية لزوّار دبي.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (84, 'طريقة عمل برنامج "نهج دبي"', 'يتم التدريب على برنامج "نهج دبي" عبر الإنترنت ويعتمد على مقاطع فيديو مع أنشطة تفاعلية وتقييمات للتأكد من مستوى معلوماتك.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (85, 'ما هي تكلفة استخدام برنامج "نهج دبي"؟', 'سيتحمّل صاحب العمل تكاليف برنامج "نهج دبي" ويتعيّن عليك التسجيل في هذا البرنامج من خلال صاحب العمل الذي سيزوّدك برمز مؤسسته.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (86, 'كيف يمكنني التسجيل؟', 'إذا كنت مقيماً في دبي وتعمل فيها، يمكنك التسجيل في برنامج "نهج دبي" عن طريق الشركة أو المؤسسة التي تعمل معها. يجب أن يكون صاحب العمل مسجلاً لدى "نهج دبي" وسيتمكن من تزويدك برمز المؤسسة. بمجرّد امتلاكك رمز المؤسسة، يمكنك التسجيل باستخدام الهوية الإماراتية والبدء بالتدريب عبر منصة "نهج دبي".', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (87, 'لدي مشكلة. ماذا أفعل؟', 'تتوفر دردشة مباشرة على مدار الساعة لمساعدتك في تجربة التعلّم. يرجى الضغط على زرّ "المساعدة" للحصول على الدعم.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (88, 'ما هي المدة المطلوبة لاستكمال تدريب "نهج دبي"؟', 'يمكن أن تستغرق الدورة من بضعة أيام إلى أسابيع، استناداً إلى عدد المرات التي تشارك فيها بالصفوف. حالما تبدأ بتدريب "نهج دبي"، سيكون لديك 90 يوماً لاستكمال الدورة.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (89, 'هل برنامج "نهج دبي" مترجم إلى لغات مختلفة؟', 'يُدرَّس المستوى 2 من برنامج "نهج دبي" باللغة الإنجليزية مع ترجمة باللغتَين العربية والماندارين. أما في المستوى 1 من برنامج "نهج دبي"، فتتوفر ترجمة للحوارات باللغات الإنجليزية والعربية والهندية والأوردية والبنغالية.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (90, 'ما الذي سأحصل عليه عندما أنهي التدريب في برنامج "نهج دبي"؟', 'بصفتك خريجاً من "نهج دبي"، سوف يتمّ الاعتراف بك كـ "بطل" في البرنامج وسوف تحصل على شهادة ودبوس مُميّز تقديراً لإنجازك.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (91, 'لماذا يُعتبر إنجاز برنامج "نهج دبي" أمراً مهماً؟', 'تُعتبر السياحة عاملاً مهمّاً يساهم في تعزيز الناتج المحلي الإجمالي لدبي وتوفير فرص عمل جديدة. ومن خلال حصولك على مزيدٍ من المعرفة والمعلومات، ستكون مستعداً بشكلٍ أفضل للترحيب بالسياح وتوفير أروع التجارب لهم. وكلما كانت التجربة السياحية أفضل، زاد عدد الزوار الراغبين في العودة إلى المدينة وارتفع عدد فرص العمل المتوفّرة.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (92, 'ما هي أنواع الفعاليات التي أحتاج إلى تصريح لإقامتها؟', 'جميع الفعاليات الترفيهية والرياضية والخيرية والدينية وتلك الخاصة بقطاع الأعمال تتطلب تصريحاً صادراً من بوابة التصاريح الإلكترونية.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (93, 'ما هي الوثائق المطلوبة للتسجيل؟', 'لأغراض التسجيل، يُطلب منك فقط تقديم: نسخة عن الرخصة التجارية، نسخة عن جواز سفر منظّم الفعالية، شهادة عدم ممانعة للتسجيل ونسخة عن الهوية الإماراتية لمستخدم النظام. الحد الأقصى المسموح به لحجم الملف هو 10 ميغابايت، ولا تُقبل ملفات .html و.htm و.aspx و.exe و.xhtml و.dll.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (94, 'هل يمكنني التسجيل في النظام إذا كانت رخصتي التجارية منتهية الصلاحية؟', 'يجب أن تكون رخصتك التجارية سارية المفعول قبل أربعة أسابيع على الأقل من تاريخ بدء الفعالية. سيرسل نظام التصاريح الإلكترونية لك إشعاراً تلقائياً لتجديد رخصتك قبل ثلاثة أشهر من انتهاء صلاحيتها.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (95, 'هل يستطيع أي منظم فعاليات التسجيل لإقامة أي نوع من الفعاليات؟', 'للتقدُّم بطلب إقامة فعالية، يجب أن تنص الرخصة التجارية بوضوح على أحد الأنشطة التالية لتسجيل التصاريح الإلكترونية: الفعاليات الترفيهية: الحفلات والخدمات الترفيهية فعاليات الأعمال: تنظيم المؤتمرات والمعارض الفعاليات الرياضية: تنظيم الراليات والبطولات الرياضية والمعارض الرياضية أو الخدمات الرياضية أو إدارة الفعاليات. يجب أن تنصّ رخصة الشركة القائمة في المنطقة الحرة بوضوح على نشاط "إدارة الفعاليات" للتسجّل على بوابة التصاريح الإلكترونية. بالإضافة إلى ذلك، يمكنك التقدم بطلب الحصول على تصاريح إقامة الفعاليات ضمن المناطق الحرة.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (96, 'كم من الوقت ستستغرق الموافقة على حسابي؟', 'ستتم الموافقة عليه في غضون 24 ساعة.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (97, 'متى سأحصل على تصريح إقامة الفعالية بعد الموافقة عليه؟', 'عند الموافقة، سوف يتمّ إصدار تصريحك في غصون خمسة أيام عمل بعد استلام الرسوم.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (98, 'ماذا يحصل في حال تمّ رفض تصريحي لإقامة فعالية؟', 'يمكنك مناقشة حالتك من خلال التواصل مع قسم التصاريح الإلكترونية في دائرة الاقتصاد والسياحة بدبي عن طريق البوابة الإلكترونية.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (99, 'هل يمكنني إضافة نوع جديد من الفعاليات إلى تصريح سبق إصداره؟', 'لا، ستحتاج إلى تقديم طلب للحصول على تصريح جديد لنوع الفعالية الإضافي.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (100, 'كم مرة يمكنني تعديل التصريح الإلكتروني؟', 'يمكنك إجراء تعديلات بقدر ما تشاء، طالما أنّ الطلب يتم قبل يوم عمل واحد على الأقل من تاريخ الفعالية.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (101, 'كم تستغرق الموافقة على التعديلات وتحديث البيانات في تصريحي؟', 'عند الموافقة، سوف يتمّ تعديل تصريحك في غضون 24 ساعة.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (102, 'هل يُسمح للشركات المُسجلة في المناطق الحرة بتنظيم فعاليات في مختلف أنحاء دبي؟', 'لا يُسمح للشركات في المناطق الحرة بتنظيم الفعاليات إلّا داخل تلك المناطق، لكن يُسمح للشركات المسجلة في دبي أن تنظّم الفعاليات في أنحاء إمارة دبي ومناطقها الحرة.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (103, 'إذا كانت الشركة تحمل رخصة تجارية من إمارة أخرى غير دبي (مثل المنطقة الحرة في رأس الخيمة)، هل يخوّلها ذلك استخدام تصاريح دبي الإلكترونية؟', 'لا. يمكن فقط للشركات الحاصلة على رخصة تجارية من إمارة دبي استخدام التصاريح الإلكترونية.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (104, 'مَن يمكنه أن يقدّم طلباً للاستفادة من برنامج رعاية الفعاليات الترفيهية؟', 'منظّمو الفعاليات الذين يقيمون فعالية: تتطلّب شراء التذاكر للمشاركة مفتوحة أمام الجميع مستوفية للشروط اللازمة لتقديم الطلب أو حائزة على تصريح إقامة فعالية ترفيهية', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (105, 'ماذا سيحصل إذا رُفض طلبي؟', 'يمكنك تقديم طلب مرة أخرى إذا رُفض الطلب الخاص بفعاليتك، شرط أن يتم ذلك في غضون ثلاثة أشهر من تاريخ بدء الفعالية.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (106, 'متى يمكنني أن أقدّم طلباً للاستفادة من برنامج رعاية الفعاليات الترفيهية؟', 'يمكنك أن تقدّم طلباً للاستفادة من هذا البرنامج على الفور. للمزيد من التفاصيل، يرجى زيارة الموقع الإلكتروني www.visitdubai.com/ar/leisure-events-sponsorship', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (107, 'ما هي المهلة القصوى لتقديم طلبي في برنامج رعاية الفعاليات الترفيهية؟', 'يجب استلام الطلبات قبل أربعة (4) أسابيع على الأقل من تاريخ الفعالية.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (108, 'هل يجب أن أحصل على تصريح لإقامة فعالية قبل تقديم طلب للاستفادة من برنامج رعاية الفعاليات الترفيهية؟', 'يجب أن تكون مؤهلاً للحصول على تصريح لإقامة فعالية ترفيهية من لتقديم طلب للاستفادة من هذا البرنامج. إذا حصلت على موافقة لتلقي الدعم والتمويل، فسيُطلب منك الحصول على تصريح لإقامة فعالية لإضفاء الطابع الرسمي على العقد.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (109, 'قدّم مُنظّم الموقع أو مسؤول التذاكر طلباً للحصول على تصريح لإقامة فعاليتي. هل يمكنه أن يقدّم طلباً للاستفادة من برنامج رعاية الفعاليات الترفيهية نيابةً عني؟', 'لا، لا يمكن سوى لمنظّم الفعالية الذي تعاقد مع الفنانين تقديم طلب للاستفادة من برنامج رعاية الفعاليات الترفيهية.', 2, null, '2022-10-21 21:59:04', '2022-10-21 21:59:04');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (110, 'أنا صاحب الموقع ومنظّم الفعالية. هل يمكنني أن أقدّم طلباً للاستفادة من برنامج رعاية الفعاليات الترفيهية؟', 'نعم، ولكن عليك أن تبرز دليلاً يثبت أنك نظمّت الفعالية/تعاقدت مع الفنانين مباشرةً لإضفاء الطابع الرسمي على العقد.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (111, 'متى سأتلقى رداً على طلبي؟', 'في غضون خمسة أيام عمل.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (112, 'كيف أقدّم طلباً للحصول على تصريح لإقامة فعالية ترفيهية؟', 'يرجى الاطلاع على هذا الدليل لمساعدتك على تقديم طلب للحصول على تصريح.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (113, 'كيف ستتأكّد دائرة الاقتصاد والسياحة بدبي من أنني منظم الفعالية؟', 'عند تقييم الطلب، قد نطلب منك دليلاً يثبت أنك وقعت عقداً لإقامة الفعالية/ مع الفنان أو مؤدي العرض، وما إلى هنالك.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (114, 'إذا كنت أحمل تصريحاً لإقامة فعالية، هل أكون مؤهلاً لتقديم طلب الرعاية؟', 'يُمكن التقدّم بطلب الرعاية فقط من قبل حاملي التصاريح لإقامة فعالية عامة.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (115, 'ما هي الخطوات التالية التي يجب اتّباعها بعد أن تتم الموافقة على طلبي؟', 'اتبع الخطوات أدناه: الخطوة 1: قدّم طلباً للحصول على تصريح إقامة الفعالية وروابط شراء التذاكر في حال لم تقم بذلك بعد الخطوة 2: يجب أن تتأكد بأنّك مُسجّل كمورّد لدائرة الاقتصاد والسياحة بدبي الخطوة 3: اتفق على مزايا الفعالية مع المدير المسؤول عن فريق إقامة الفعاليات الترفيهية الخطوة 4: أضف جدول فعاليات دبي كقناة لشراء التذاكر على جميع المواد المصاحبة للفعالية وعلى الموقع الإلكتروني والمواد الإعلانية وإلى ما ذلك. الخطوة 5: أكمل عقدك مع دائرة الاقتصاد والسياحة بدبي بما فيها نسخة عن رخصتك التجارية الخطوة 6: سوّق لفعاليتك ونفّذها مع الحرص على توفير المزايا المتفق عليها الخطوة 7: اعمل مع شركة التذاكر المتعاقد معها لاحتساب سعر التذاكر والمدفوعات مع دائرة الاقتصاد والسياحة بدبي الخطوة 8: عند استلام طلب الشراء المحلي لمبلغ الرعاية من جانب دائرة الاقتصاد والسياحة بدبي، يرجى إصدار فاتورة طبقاً للتعليمات المتعلقة بتسديد ثمن الرعاية', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (116, 'هل يمكنني الحصول على تمويل مسبقاً؟', 'كلا، سيتم دفع المبلغ المخصص للرعاية في غضون ثلاثة (3) أيام عمل بعد احتساب رسوم تذاكر الفعالية مع دائرة الاقتصاد والسياحة بدبي.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (117, 'هل ما زلت مؤهلاً للرعاية إن كان لديّ عقد موقّع وتمّ تأجيل الفعالية الخاصة بي إلى تاريخٍ آخر؟', 'نعم، ولكن يجب عليك إبلاغ مدير حسابك رسمياً حول مسألة تغيير التاريخ وستتمّ إضافة ملحق إلى العقد الحالي.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (118, 'ماذا يحدث إذا كان لدي عقد رعاية موقّع وتم إلغاء فعاليتي؟', 'سيتم فسخ العقد الموقّع معك، إذ لا يُدفع التمويل إلا عند إتمام الفعالية مع دائرة السياحة والتسويق التجاري.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (119, 'إذا كنت أنا مالك الموقع ومنظم الفعالية في الوقت عينه (شراكة بين المنظم الدولي والمحلي)، هل أحصل على الرعاية أيضاً؟', 'يمكن لطرفٍ واحد فقط التقدم بطلب رعاية لكل فعالية. وفي حال وجود أي شكوك، فسنطلب بياناً موقّعاً من الطرف المتعاقِد يفيد بأنّ لديه الحق في التصرف بالنيابة عن الطرف الآخر، والتعاقد معه لتنظيم الفعالية.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (120, 'ما هو شريك بيع التذاكر في نظام API؟', 'يشير الشريك في نظام API إلى شركات بيع وتسجيل التذاكر التي تعمل مع دائرة الاقتصاد والسياحة بدبي لبيع التذاكرأو تلقّي طلبات التسجيل في الفعاليات التي تقام في إمارة دبي. يجب إجراء ربط تقني كامل بين نظام شريك بيع التذاكر ونظام دائرة الاقتصاد والسياحة بدبي لضمان الالتزام باللوائح التنظيميّة الحالية.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (121, 'هل شركتي مؤهلة لتكون شريكة بيع التذاكر في نظام API؟', 'أي شركة جديدة أو قائمة تمتلك نظاماً لبيع التذاكر أو التسجيل في الفعاليات، وتحمل رخصة تجارية سارية المفعول في دبي، تُعتبر مؤهلة لتكون بائع تذاكر معتمد بعد الانتهاء من عمليات الربط والدمج المطلوبة.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (122, 'ما هي المتطلبات اللازمة لأكون شريكاً معتمداً لبيع التذاكر في نظام API؟', 'للعمل عن طريق نظام API، أنت بحاجة إلى: رخصة تجارية سارية المفعول في دبي أن تكون الأنشطة المُدرجة في الرخصة التجارية تتعلق بتنظيم الفعاليات نظام جاهز لإصدار التذاكر والتسجيل في الفعاليات اتفاقية موقّعة لبيع التذاكر عبر نظام API (المقدم من دائرة الاقتصاد والسياحة بدبي) تقديم استمارة طلب الاشتراك في نظام API.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (123, 'هل يجب سداد أي رسوم لكي أصبح شريكاً في نظام API؟', 'يجب عليك تسديد رسوم تُدفع لمرة واحدة وغير قابلة للاسترداد بقيمة 10,000 درهم إماراتي.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (124, 'كم من الوقت تستغرق عملية الربط مع نظام API؟', 'تستغرق عملية الاندماج بين أسبوعين وثمانية أسابيع بحسب مستوى استجابة فريقك الفني.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (125, 'ما هو نظام eTix؟', 'يتوفر نظام eTix على منصتنا الإلكترونية وهو عبارة عن طلب إلكتروني يسهّل تقديم إشعارات الفعاليات الجديدة إلكترونياً من قِبل منظمي الفعاليات. يرجى الانتباه إلى أنّ الإشعارات الورقيّة الخاصّة بالفعاليات الجديدة لم تعد مقبولة من دائرة السياحة والتسويق التجاري في دبي.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (126, 'كم يستغرق تفعيل حسابي على نظام eTix؟', 'سوف يتم تفعيل الحسابات في غضون يوم عملٍ واحد حالما يتمّ التحقق من صحة عنوان البريد الإلكتروني. إذا لم تستلم بريداً إلكترونياً للتحقق، يرجى التواصل معنا.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (127, 'هل يمكن أن تمتلك شركتي عدة مستخدمين على نظام eTix؟', 'يجوز لشركة تنظيم الفعاليات امتلاك عدّة مستخدمين على النظام ويجوز لهم صياغة إشعارات الفعاليات الجديدة وإرسالها بالنيابة عنها. يجب على الشركة توفير شهادة عدم ممانعة رسمية لكل مستخدم يردُ فيها الاسم الكامل للمتقدّم بالطلب ورقم الهوية الإماراتية. كما يتعيّن على كل مستخدم أيضاً التسجُّل بشكلٍ مستقلّ من خلال عنوان بريد إلكتروني وإرفاق نسخة عن الهوية الإماراتية وشهادة عدم الممانعة.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (128, 'ما نوع التعديلات التي يمكنني إجراؤها على فعاليتي عبر نظام eTix؟', 'يمكنك إضافة التواريخ أو تغييرها وكذلك الأمر بالنسبة للمشاركين والموقع.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (129, 'ما هو نظام إصدار التذاكر الفورية؟', 'يُستخدم هذا النظام الجديد بدلاً من عملية ختم التذاكر اليدوية للدخول إلى مواقع إقامة الفعاليات الترفيهية، كما يُتيح تنفيذ الإجراءات بشكل سريع، ويسمح لمديري الفعاليات والمواقع بإصدار التذاكر فوراً.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (130, 'كيف يعمل نظام الإصدار الفوري للتذاكر؟', 'بعد التسجيل وإنشاء حساب "مستخدم مميّز"، يمكنك إضافة مستخدمين وتحديد فئات التذاكر والأسعار ووضع تصميم التذاكر. يجب أن تتوفر في الموقع طابعة لإصدار التذاكر (تتوفر قائمة بالتذاكر والطابعات المدعومة). يتمّ احتساب رسوم دائرة الاقتصاد والسياحة بدبي تلقائياً من خلال فاتورة إلكترونية تصدر شهرياً ويتمّ تسديدها عبر الإنترنت.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (131, 'كيف أبدأ بطباعة التذاكر الفورية؟', 'اشترِ طابعة تذاكر وتذاكر فارغة من مورّد معتمد، وقُم بوضع اللمسات النهائية على تصميم التذاكر الخاص بك. بعد ذلك، قم بإنشاء حساب بصفة "مُشرف" باستخدام استمارة التسجيل الجديدة على منصة إصدار التذاكر الفورية. لتفعيل حسابك، قُم بزيارة مكاتب دبي للسياحة مُصطحباً معك إشعار البريد الإلكتروني المُعتمد أو الرقم المرجعي للطلب مع وثائق التفويض المطلوبة وبطاقة مندوب العلاقات الحكومية وخطاب التفويض الرسمي الذي تعيّنت بموجبه مُشرفاً على نظام الشركة، بالإضافة إلى نسخة عن الهوية الإماراتية.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (132, 'هل يمكنني استخدام التذاكر التي تحمل علامتي التجارية الخاصة في نظام إصدار التذاكر الفورية؟', 'نعم، طالما أنك تتبع الإرشادات الواردة في تصميم النموذج. يجب أن تقوم بإرسال تصميمك عبر الإنترنت للحصول على الموافقة النهائية من دائرة الاقتصاد والسياحة بدبي.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (133, 'هل يمكن بيع تذاكر الدخول الفورية إلى موقع الفعاليّة عبر الإنترنت؟', 'لا يدعم النظام مبيعات التذاكر على الإنترنت. يمكن بيع التذاكر إلكترونيّاً من خلال شركاء بيع التذاكر المُعتمدين واستبدالها عند الباب بتذكرة دخول إلى الموقع.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (134, 'هل يمكن إعادة طباعة التذاكر الفوريّة أو إرجاعها أو استرداد قيمتها؟', 'لا. تخضع كل تذكرة مطبوعة لرسوم دائرة الاقتصاد والسياحة بدبي بنسبة 10%.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (135, 'ما هو بيت العطلات؟', 'يُعتبر بيت العطلات أي مسكن مجهز بالأثاث يقع داخل مدينة دبي أو في المناطق الحرة (استوديو أو عدة غرف نوم) يتمّ تأجيره للضيوف استناداً إلى شروط ومعايير مُحدّدة.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (136, 'هل تعتبر الفنادق أو الشقق الفندقية بيوتاً للعطلات؟', 'لا. يتمّ إدراج الفنادق والشقق الفندقية ضمن تصنيفٍ مختلف. اطّلع على خدمة تصنيف الفنادق هنا', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (137, 'كم من الوقت ستستغرق الموافقة على تسجيل بيت العطلات الخاص بي؟', 'بمجرّد مراجعة طلب التسجيل والموافقة عليه، سيتمّ إرسال رابط الدفع للمالكين الفرديين في غضون يوم عملٍ واحد. سيتم تفعيل التسجيل حالما يتمّ تأكيد عملية الدفع، وسيتم تفعيل حسابات المنظّمين في غضون يوم عملٍ واحد من التسجيل.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (138, 'هل يمكنني تسجيل بيوت عطلات بموجب رخصة عقارية أو رخصة تنظيم جولات سياحية داخلية؟', 'لا. يجب أن تتضمّن رخصتك التجارية "تأجير بيوت العطلات" كنشاط محدد.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (139, 'ما هي فترة صلاحية تسجيل بيت العطلات؟', 'لا يوجد تاريخ لانتهاء الصلاحية ولكن يتعيّن عليك تحديث الرخصة التجارية سنوياً إضافة إلى أي معلومات أخرى ذات صلة. لا يوجد أي رسوم للتجديد.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (140, 'بصفتي مستأجراً، هل أنا بحاجة إلى تسجيل عقد الإيجار الخاص بي في نظام "إيجاري"؟', 'نعم، أنت بحاجة إلى عقد إيجار مُسجّل في نظام "إيجاري".', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (141, 'لقد قمت مؤخراً بتغيير حسابي في هيئة كهرباء ومياه دبي ولا أملك فاتورة حديثة.', 'يمكنك إرسال كشف حساب صادر عن هيئة كهرباء ومياه دبي أو تسجيل الدخول إلى حساب (ديوا) والتقاط صورة شاشة لملفّ التعريف الخاص بحسابك. يجب أن تُظهر صورة الشاشة مالك الحساب ورقم الحساب في هيئة كهرباء ومياه دبي.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (142, 'ماذا عليّ أن أقدّم في حال كنت لا أمتلك سند ملكية؟', 'اتفاقية البيع والشراء وإمّا شهادة أو خطاب إتمام الدفع (العنوان: لمن يهمه الأمر) من المطوّر العقاري أو مالك (مالكي) المبنى يرد فيها ما يلي: 1. عدم توفُُّر سند ملكية لهذه الوحدة 2. في حال كان تسديد ثمن الوحدة يتم عبر خطة سداد، فاذكر ذلك مع الإشارة إلى أنه ليس هنالك مبالغ مستحقة 3. اذكر أنّ الوحدة سكنية', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (143, 'هل تُعتبر جميع الفلل مؤهلة للحصول على تصاريح بيوت العطلات؟', 'لا. يجب أن تكون الفلل السكنية ضمن مجمّع مسوّر يشمل أربع فلل كحدّ أدنى. كما يمكن أن تعتبر دائرة الاقتصاد والسياحة بدبي الفلل التجارية الواقعة على الطرق الرئيسية بيوتاً للعطلات.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (144, 'ما هي مدة صلاحية تصريح بيوت العطلات؟', 'يتمّ إصدار تصريح بيوت العطلات لثلاثة أشهر على الأقل و12 شهراً كحدّ أقصى، وتكون رسوم التصريح بمعزل عن فترة الصلاحية.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (145, 'انتهت صلاحية تصريحي وتمّت إزالته من النظام. كيف يمكنني استعادته؟', 'في حال انتهت صلاحية تصريحك، فقد تمّ حذف الوحدة الخاصة بك من النظام نهائياً. يجب عليك إعادة تقديم الطلب لإضافة وحدة جديدة.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (146, 'متى يمكنني تقديم طلب لتجديد تصريح الوحدة الخاص بي؟', 'سيظهر رمز التجديد قبل 30 يوماً من انتهاء صلاحية تصريحك الحالي. سيؤدي عدم التجديد إلى إزالة الوحدة الخاصة بك من النظام وسيتعين عليك إعادة تقديم الطلب لإضافة وحدة جديدة.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (147, 'لماذا يتعين عليّ إلغاء تصريح بيوت العطلات الخاص بي؟', 'إذا تمّ إدراج الوحدة الخاصة بك كبيت عطلات ولم يعد كذلك، عندها لا تعود اللوائح والالتزامات المتعلقة ببيت العطلات سارية عليك وبالتالي يجب إلغاء تصريحك.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (148, 'هل هناك متطلبات إدارية أخرى عند إلغاء تصريحي؟', 'يجب تسديد جميع رسوم درهم السياحة المستحقة.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (149, 'هل ستخضع تواريخ إصدار تصريحي الحالي وانتهاء صلاحيته للتغيير؟', 'لا. التصريح المُحدّث يعكس التغييرات التي تطرأ على نوع التصنيف أو عدد الغرف فقط. أما تواريخ إصدار التصريح الأصلي وانتهاء صلاحيته فتبقى نفسها.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (150, 'هل سيتمّ تطبيق الرسوم الإضافية في حال أضفتُ غرفاً؟', 'نعم. يتمّ تطبيق رسوم إدارية على جميع التحديثات التي تتمّ عبر الإنترنت. سيتمّ أيضاً فرض رسوم إضافية بناءً على عدد الغرف التي تمّت إضافتها فضلاً عن رسوم درهم السياحة المستحقة.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (151, 'هل يمكنني تعديل إسم المبنى الخاص بي؟', 'لا. لا يمكن تحديث تفاصيل المبنى هنا. يرجى التواصل مع فريق الدعم الفني للمساعدة.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (152, 'كم من الوقت يستغرق تحديث تصريحي؟', 'يتمّ تطبيق التحديثات بمجرّد الانتهاء من عملية الدفع بنجاح. عندها، يُمكن طباعة التصريح الجديد على الفور.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (153, 'ما هي الوثائق المطلوبة لإضافة مستخدم جديد؟', 'نسخة محدّثة عن الرخصة التجارية للمُشغّلين ونسخة عن جواز السفر أو الهوية الإماراتية للمدير المُعيّن من قِبل المُشغّل أو الموظفين الإداريين.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (154, 'ما هي فترة الصلاحية للمستخدم الجديد؟', 'تستمر صلاحية المستخدم حتى يتمّ إلغاء تفعيله في النظام.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (155, 'ما هي الفترة المطلوبة لتفعيل مستخدم جديد؟', 'في غضون 24 ساعة.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (156, 'ما هو عدد المدراء الذين يمكن إدراجهم كمشغّلين لبيوت العطلات؟', 'أنت بحاجة إلى مدير واحد على الأقل ويمكن أن يكون لديك العديد منهم. ولكن، يجب تحديدهم على الرخصة التجارية الخاصة بك.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (157, 'هل يمكن تعديل مالك الوحدة على التصريح الخاص بي؟', 'لا. يمكن فقط تعديل نوع التصنيف أو عدد الغرف في الوحدة على التصريح.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (158, 'لديّ مدير جديد على الرخصة التجارية، كيف يمكنني تحديث ذلك على النظام؟', 'أضف مديراً جديداً من خلال اتباع الخطوات الموضحة أعلاه. بمجرّد الموافقة وتفعيله كمستخدم، يجب عليك إلغاء تفعيل المدير السابق.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (159, 'ما هو الفرق بين المُشرف والمُدير؟', 'يمتلك المدير حق الوصول الكامل إلى النظام ويجوز له إضافة الخصائص وإزالتها وتحديثها. يمكن للمُشرف تسجيل وصول الضيوف ومغادرتهم فقط.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (160, 'أنا أواجه مشكلةً فنية.', 'تواصل مع فريقنا المُتخصّص بالخدمات الإلكترونية الذي سيسرُّ بمساعدتك على حلّ أي مشاكل تواجهها.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (161, 'كم من الوقت تستغرق عملية التصنيف؟', 'سوف يقوم فريق التفتيش في دائرة الاقتصاد والسياحة بدبي بالاتصال بك وزيارتك في غضون يوم عملٍ واحد من إرسال طلبك ليتمّ بعد ذلك الموافقة على التصنيف أو رفضه.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (162, 'كم من الوقت تستغرق عملية التجديد؟', 'سوف يقوم فريق التفتيش التابع لدائرة الاقتصاد والسياحة بدبي بزيارة الفندق الخاص بك في غضون 24 ساعة من تقديم الطلب (إذا لزم الأمر)، وسوف يتمّ إصدار شهادة التصنيف التي تمّ تجديدها فور استيفاء الفندق للمعايير.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (163, 'في حال لم أستوفِ متطلبات تصنيف الفنادق، هل يمكنني أن أطلب فترة سماح؟', 'يمكن منح فترة سماح في بعض الظروف، ويتمّ تقييم كل حالة على حِدة.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (164, 'ما هو التقييم الذاتي لتصنيف الفنادق؟', 'أداة مفيدة يجب أن يستخدمها الفندق لتقييم مدى ملاءمته لمعايير دائرة الاقتصاد والسياحة بدبي والتزامه بالحد الأدنى من المتطلبات. ليس من الضروري إرسال التقييم الذاتي كوثيقة داعمة.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (165, 'متى يمكنني البدء بالعمل في الفندق بعد التصنيف؟', 'بعد الحصول على التصنيف، يتعيّن عليك التقدم بطلب الحصول على رخصة السياحة الفندقية من دائرة الاقتصاد والسياحة بدبي، ثم عليك التسجيل في برنامج الدرهم السياحي من خلال دائرة الاقتصاد والسياحة بدبي. وبعد ذلك، يمكنك التقدُّم بطلب "استقبال الضيوف" لبدء العمل في الفندق عن طريق بوابة التصنيفات الإلكترونية.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (166, 'متى يتعيّن عليّ تجديد شهادة التصنيف الخاصة بي؟', 'قبل تاريخ انتهاء الصلاحية بثلاثة أشهر.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (167, 'أين يمكنني العثور على شهادة التصنيف التي تمّ تجديدها؟', 'سوف تتوفر الشهادة في الملف الخاص بك على بوابة التصنيف، ويُمكنك طباعتها.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (168, 'لماذا تمّ رفض التصنيف الفندقي الخاص بي بعد المعاينة والتفتيش؟', 'لم يستوفِ الفندق متطلبات التصنيف المطلوب، كما هو مفصّل في التقرير الذي يُحدّد الثغرات ذات الصلة والتي يجب معالجتها قبل إعادة المعاينة مرة جديدة.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (169, 'كم من الوقت يتطلب حصولي على التصنيف المُعدّل؟', 'سوف يقوم فريق التفتيش والمعاينة في دائرة الاقتصاد والسياحة بدبي بزيارتك في غضون 24 ساعة من إرسال طلبك وسوف تتمّ بعد ذلك الموافقة على التصنيف المعدّل.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (170, 'كم يستغرق الحصول على شهادة عدم الممانعة الخاصة بي؟', 'سوف يقوم فريق المعاينة والتفتيش في دائرة الاقتصاد والسياحة بدبي بالاتصال بك وزيارتك في غضون يوم عملٍ واحد من إرسال طلبك لتتمّ بعد ذلك الموافقة على التصنيف أو رفضه.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (171, 'ما هي مدة صلاحية خطاب الموافقة/شهادة عدم الممانعة؟', 'تُعتبر شهادة عدم الممانعة صالحة لمدة ستة أشهر على أن يتمّ استخدامها خلال هذه المدة للحصول على تصاريح البناء المطلوبة. وإذا لم يتمّ استخدامها خلال هذه الفترة، عندها يجب إصدار خطاب جديد.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (172, 'متى يجب أن أقدّم طلباً للحصول على شهادة عدم ممانعة للبناء؟', 'عند بناء عقارٍ جديد أو تحويل بناء قائم إلى فندق أو تغيير نوع استخدام قطعة أرض. يجب أولاً الحصول على شهادة عدم الممانعة قبل بدء الأعمال.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (173, 'هل يتم توفير أي دعم استشاري؟', 'تقدم دائرة الاقتصاد والسياحة بدبي خدمات استشارية تساعد الفنادق على استيفاء معايير التصنيفات المطلوبة، كما تُحدّد الثغرات ومجالات التحسين.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (174, 'إذا قدّمت طلباً للحصول على رخصة سياحية، هل سيساعد ذلك على البدء بعملية التصنيف؟', 'لا. يجب أولاً تسجيل الفندق عن طريق بوابة التصنيف الإلكترونية لبدء عملية التصنيف. وحالما يتمّ تسجيله، يجب توفير رقم الرخصة السياحية أو رقم طلب الترخيص كخطوة تالية لدى تقديم الطلب للحصول على تصنيف الفندق.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (175, 'أي جواز سفر وهوية إماراتية يجب إدخالها في النظام؟', 'نطلب جواز سفر المدير الذي يختاره القيّمون على الفندق وهويته الإماراتية مرفقةً بخطاب تفويض من مالك أو مستثمر الفندق، الأمر الذي يسمح له بالدخول إلى بوابة التصنيف الإلكترونية واستخدامها.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (176, 'ماذا لو كنتُ أمتلك العديد من العقارات على رقم المعاملة نفسها؟', 'يجب تسجيل كل منشأة/فندق على حدة وإنشاء ملف تعريف منفصل حتى ولو كانت العقارات تشغل قطعة الأرض نفسها. التسجيل مطلوب لكل عقار وليس لكل مالك.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (177, 'ما هو خطاب التفويض؟', 'يمنح خطاب التفويض المدير، الذي يختاره القيّمون على الفندق، إذن الدخول إلى بوابة التصنيف الإلكترونية واستخدامها بالنيابة عن المالك.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (178, 'ما هو رقم المعاملة الخاص بي؟', 'يُعتبر رقم المعاملة الخاص بك رقم الموافقة المبدئية على تنفيذ مشروعك. تُعتبر الموافقة المبدئيّة شرطاً أساسياً لبدء الأعمال وهي مطلوبة للتسجيل على بوابة التصنيفات الإلكترونية.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (179, 'ما هو إطار عمل خدمات الاستشارات الفندقية؟', 'يمكنك تحديد موعد لاجتماع في موقع الفندق أو مقابلة الفريق في مكاتبنا. يرجى طلب موعد من خلال بوابة التصنيف.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (180, 'ما هي الرسوم المطلوبة للحصول على خدمات الاستشارات الفندقية؟', 'تُفرض رسوم بقيمة 1000 درهم إماراتي للساعة الواحدة باستثناء رسوم الابتكار والمعرفة التي تطبّقها حكومة دبي.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (181, 'لماذا عليّ استخدام خدمة الاستشارات الفندقية؟', 'تمّ تأسيس الخدمات الاستشارية في دائرة الاقتصاد والسياحة بدبي لدعم شركائنا ومالكي المنشآت في قطاع الضيافة، وحرصاً منّا على أن تستوفي مواصفات الفندق الخاص بك المتطلبات اللازمة، خلال عملية البناء وقبل الافتتاح.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (182, 'هل سأتلقّى تقريراً استشارياً بعد الاجتماع؟', 'نعم. بعد اجتماعنا، سوف يتمّ تحميل تقرير استشاري إلى الملف الشخصي الخاص بك على بوابة التصنيف الإلكترونية.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (183, 'أنا أقوم بإجراء تغييرات على عدد الغرف وقائمة الجرد. ما هي الوثائق التي أحتاج إليها؟', 'سوف تكون بحاجة إلى كتاب رسمي موقّع ومختوم من الشركة على ورقة رسمية يفصّل التغييرات التي ترغب في القيام بها.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (184, 'إذا كنتُ أنوي عدم تشغيل الغرف، فهل سيغيّر ذلك تصنيف الفندق الخاص بي؟', 'لا. لن يؤثر إغلاق الغرف على تصنيف الفندق الخاص بك.', 2, null, '2022-10-21 21:59:05', '2022-10-21 21:59:05');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (185, 'إذا قمتُ بإزالة الغرف من قائمة الجرد بشكلٍ دائم، كيف سيؤثر ذلك على رسوم التجديد؟', 'يستند التجديد السنوي إلى العدد الإجمالي للوحدات، وبالتالي ستنعكس إزالة الغرف على رسوم التجديد الخاصة بك.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (186, 'هل سأتمكن من إتاحة غرف جديدة لحجوزات الضيوف حالما تتمّ الموافقة على طلب تغيير عدد الغرف؟', 'لا. يتعيّن عليك تقديم طلب للحصول على شهادة عدم ممانعة "استقبال ضيوف جدد" لجميع الغرف الجديدة المُضافة، وانتظار انتهاء عملية التفتيش الإضافية بنجاح قبل أن تصبح الغرف مُتاحة للاستخدام.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (187, 'هل يمكنني تلقّي الحجوزات في الفندق قبل حصولي على شهادة عدم الممانعة؟', 'لا يمكنك العمل من دون الحصول على شهادة عدم ممانعة رسمية من دائرة الاقتصاد والسياحة بدبي، لذلك لن تتمكن من قبول حجوزات الضيوف.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (188, 'لماذا تمّ رفض شهادة عدم ممانعة استقبال الضيوف بعد عملية التفتيش؟', 'لم يستوفِ الفندق المتطلبات المطلوبة لاستقبال الضيوف. سوف يوفّر فريق التفتيش التابع لدائرة الاقتصاد والسياحة بدبي تقريراً بعد الانتهاء من عملية التفتيش حيث يحدّد الثغرات ذات الصلة والتي يجب معالجتها قبل عملية إعادة التفتيش.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (189, 'ماذا يجب أن أفعل بعد أن أقوم بالتسجيل في برنامج "مضياف"؟', 'بعد أن تقوم بالتسجيل، يمكنك حجز جلسة مضياف الاستشارية لمدة نصف ساعة حيث سنساعدك على فهم خدماتنا بالتفصيل مثل الإرشاد الوظيفي والتدريب على تنمية المهارات والتطوّع وبرامج التدريب والمُخيّمات الطلابيّة الصيفية/الشتوية فضلاً عن برنامج المرشد السياحي الإماراتي. ستحصل على معلومات شاملة عن هذه الخدمات وكيف ستساعدك على دخول قطاع السياحة.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (190, 'هل يدعم برنامج "مضياف" الطلاب فقط، أم الخريجين أيضاً؟', 'يدعم البرنامج الطلاب والخرّيجين. يساعد مضياف جميع الطلاب المهتمين بالعمل في قطاع السياحة من خلال برامج التدريب الصيفية والشتوية أثناء العطلات المدرسية وبرامج التدريب وفرص التطوّع. يدعم مضياف الخرّيجين أيضاً من خلال فرص التوظيف بدوامٍ كامل أو جزئي مع الشركاء في قطاع السياحة.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (191, 'كيف يمكنني أن أصبح مرشداً سياحياً إماراتياً؟', 'يمتدّ برنامج تدريب "المرشد السياحي الإماراتي" المجاني على مدى تسعة أيام، يرجى زيارة موقع www.tourguidetraining.ae للتسجيل.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (192, 'هل يمكن لشركتي إقامة عرض ترويجي كجزء من أحد المهرجانات في دبي؟', 'نعم، يُمكن للشركات التي تخطط لإقامة عرض ترويجي إرسال طلبها مع جميع التفاصيل والوثائق والرسوم المطلوبة من خلال اتباع الخطوات المذكورة أعلاه لاستلام تصريح إقامة عرض ترويجي.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (193, 'ما هي أنواع العروض الترويجية التي يمكنني تقديمها خلال أحد المهرجانات في دبي؟', 'تشمل العروض الترويجية الصفقات التي تركز على الأسعار (مثل التنزيلات والتخفيضات والخصومات والتصفيات، وما إلى ذلك)، والعروض الخاصة (مثل بيع أكثر من منتج ضمن مجموعة واحدة أو تقديم هدية عند الشراء، وغيرها من العروض) بالإضافة إلى السحوبات والمسابقات وأكشاك البيع الصغيرة ومنافذ البيع المتنقلة.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (194, 'ما هي مدة صلاحية التصريح الخاص بالعروض الترويجية؟', 'بمجرّد تسديد رسم الاشتراك في جدول الفعاليات السنوية، يكون تصريحك صالحاً لمدة 12 شهراً ويغطي فعاليات موسمية محدّدة مثل مهرجان دبي للتسوق ومفاجآت صيف دبي ومهرجان دبي للمأكولات وغيرها. نقدم أيضاً باقات خارج إطار جدول الفعاليات السنوية مقابل رسوم وشروط مختلفة حيث يستطيع شركاء البيع بالتجزئة أيضاً طرح عروض ترويجية.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (195, 'هل يمكنني التقدم بطلب للحصول على تصاريح متعدّدة للعروض الترويجية، أو المشاركة في عدّة مهرجانات في الوقت نفسه؟', 'نعم. يجوز لك تحديد المهرجانات التي ترغب في تقديم عروض ترويجية خلالها ومن ثم إرسال تفاصيل العروض الخاصة بكل مهرجان.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (196, 'ما هي القنوات المتوفرة لتسديد الرسوم الخاصة بتصاريح العروض ترويجية؟', 'يجب تسديد رسوم طلب الحصول على التصريح قبل إرسال تفاصيل العروض الترويجية. يمكن تسديد الرسوم عبر الإنترنت أو البطاقة الائتمانية أو عبر الأنصاري للصرافة، نقداً أو بواسطة شيك مصرفي.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (197, 'هل يمكنني إجراء تعديلات على تصريحي الخاص بإقامة عروض ترويجية؟', 'يمكنك إجراء تعديلات بعد تلقّي التصريح من خلال إرسال طلب إلى retailcalendar@dubaitourism.ae', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (198, 'أودّ المشاركة ولكن لن أتمكّن من حضور الفعالية بكاملها. هل ما زال بإمكاني التقدُّم بطلب؟', 'نعم، تتوفر أماكن محدودة لهذه الحالات. يرجى التواصل معنا لمعرفة متطلباتك.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (199, 'هل تقدمون الدعم من أجل ابتكار التصميم وتكوين العلامة التجارية لفعاليتي؟', 'بمجرّد الموافقة على مشاركتك، تتوفر خدمات الدعم مجاناً. تواصل مع فريق التنظيم الخاص بك وحدّد طلبك.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (200, 'هل يمكنني مشاركة مساحة الفعالية الخاصة بي مع بائعين آخرين؟', 'لا يمكن لبائعي المأكولات والمشروبات مشاركة مساحة الفعالية الخاصة بهم، ولكن يمكن لبائعي التجزئة القيام بذلك. تواصل مع فريق التنظيم الخاص بك وحدّد طلبك.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (201, 'ليس لديّ عدد كافٍ من الموظفين لمساعدتي في فعاليتي. هل توفرون موظفين لهذه الغاية؟', 'لا نوفر موظّفين للمساعدة إذ يقع ذلك على عاتق المشارك.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (202, 'هل يمكنني إدارة ورشة العمل أو النشاط الخاص بي؟', 'نعم ونحن نشجع على ذلك. يرجى التواصل معنا ومشاركتنا أفكارك حالما تتمّ الموافقة على مشاركتك.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (203, 'ما هي متطلبات الصحة والسلامة التي يجب أن أستوفيها لتنظيم فعاليتي؟', 'بالنسبة إلى عالم مدهش، يُطلب من جميع مشغّلي الملاهي الراغبين في المشاركة الحصول على كلّ شهادات الصحة والسلامة اللازمة. كما يجب أن يكون جميع الموظفين مؤهلين وحائزين على شهادات في هذا المجال.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (204, 'إلى أي مدى يمكن التقدُّم مسبقاً بطلب للمشاركة في فعالية تقيمها دائرة الاقتصاد والسياحة بدبي؟', 'نشجع جميع البائعين على تقديم الطلبات قبل شهرين على الأقل من موعد الفعالية.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (205, 'أين تُقام دورة تدريب سائق رحلات السفاري؟', 'يقام التدريب في كلية دبي للسياحة الواقعة في المقرّ الرئيسي لدائرة الاقتصاد والسياحة بدبي: مبنى "ون سنترال 2"، الطابق 4. في حال كانت هنالك حاجة إلى موقعٍ بديل، سوف يتمّ إبلاغ المشاركين بذلك قبل انطلاق الدورة.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (206, 'بأي لغات تقام دورة سائق رحلات السفاري؟', 'تُقام الدورة باللغة الإنجليزية، لأن القدرة على التواصل بفعالية مع الزوار والسياح من مختلف أنحاء العالم تُعتبر أمراً هاما لتقوم بعملك بالشكل المطلوب. يُعتبر إثبات إجادة اللغة الإنجليزية عند التقدُّم بالطلب للمشاركة في الدورة من أبرز الشروط.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (207, 'أين يمكنني العثور على أحدث وأفضل المعلومات حول القيادة الصحراوية ورحلات السفاري لمساعدتي على التحضير للامتحان؟', 'تشمل المنصة التدريبيّة للمرشدين السياحيّين جميع المواد التي قد تحتاج إليها. يمكنك الاطلاع عليها بعد تسديد رسوم الدورة.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (208, 'كيف يمكنني تعزيز فرص نجاحي في التدريب الخاص بسائقي رحلات السفاري؟', 'يتطلب الأمر مهاراتٍ في القيادة وإلماماً في السيارات، والأهم من ذلك هي القدرة على التواصل مع ضيوفك بمعرفة وثقة. لذلك، عزّز مهاراتك الاجتماعية وكفاءتك اللغوية. يمكنك العثور على مواد مفيدة تساعدك على التحضير للامتحان عبر منصة التدريب الخاصة برحلات السفاري.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (209, 'ماذا لو فشلت في امتحان الإعادة؟', 'في حال فشلت في هذا الاختبار، يُسمح لك بإعادته. ويبلغ رسم الإعادة 120 درهماً إماراتياً (100 درهم إماراتي + رسم معرفة 10 دراهم إماراتية + رسم ابتكار 10 دراهم إماراتية).', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (210, 'ما هي مدة صلاحية تدريب سائق رحلات السفاري؟', 'يُعتبر تدريب سائق رحلات السفاري صالحاً لعامٍ واحد، وبعد هذه المدة يتعيّن عليك تجديده. يمكنك التقدُّم بطلب الحصول على تصريح القيادة الصحراوية من هيئة الطرق والمواصلات طوال فترة صلاحية شهادة التدريب الخاصة بك.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (211, 'أين يمكنني الحصول على نسخة من شهادة تدريب سائق رحلات السفاري؟', 'يمكنك طباعة شهادتك عبرنظام المرشدين السياحيين لرحلات السفاري الصحراويّة.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (212, 'هل أستطيع البدء بقيادة مركبات رحلات السفاري مباشرة بعد أن أنجح في دورة سائق رحلات سفاري؟', 'لا، عليك أولاً الحصول على تصريح القيادة الصحراوية من هيئة الطرق والمواصلات في دبي حيث أن كلية دبي للسياحة توفّر التدريب فقط. بعد نجاحك في دورة سائق رحلات سفاري، سوف تقوم كلية دبي للسياحة بتحديث ملف المرور الخاص بك مباشرةً لدى هيئة الطرق والمواصلات في دبي. عندها، يمكنك التقدُّم بطلب للحصول على تصريح القيادة الصحراوية قبل قيادة أي مركبات سفاري.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (213, 'هل يمكنني استخدام رخصة قيادة مركبات السفاري الصادرة في إمارة أخرى؟', 'نعم، يمكنك ذلك. تُطبّق الشروط والأحكام', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (214, 'إذا حصلت على رخصة سائق رحلات السفاري، هل أحصل تلقائياً على رخصة الإرشاد السياحي أيضاً؟', 'لا، شهادة الإرشاد السياحي منفصلة ويمكنك تقديم طلب التدريب عبر بوابة تدريب المرشدين السياحيين. يُعتبر اجتياز التدريب الخاص بسائقي رحلات السفاري شرطاً إلزامياً قبل أن تتمكن من تقديم طلب للحصول على تصريح القيادة الصحراوية من هيئة الطرق والمواصلات.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (215, 'كم تبلغ كلفة تجديد شهادة تدريب سائق رحلات السفاري؟', 'تبلغ كلفة دورة التجديد والامتحان 520 درهماً إماراتياً بما فيها الرسوم والضريبة.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (216, 'هل يمكنني المطالبة باسترداد رسوم دورة سائق رحلات السفاري أو تحويل الرصيد إلى دورة أخرى؟', 'رسوم الدورة غير قابلة للتحويل أو الاسترداد.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (217, 'ما هي الوثائق المطلوبة لتسجيل مركبات رحلات السفاري؟', 'لطلب تسجيل مركبات رحلات السفاري، يجب أن تستوفي شركتك الشروط التالية: - خمسة سائقين مسجلين على الأقل - سائقان على الأقل يحملان شهادة إسعافات أوليّة - خمس مركبات على الأقل مُخصّصة لرحلات السفاري ومملوكة من قِبل الشركة، ومزوّدة بنظام تحديد المواقع - وثيقة تأمين شاملة للشركة', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (218, 'ما هي مدة صلاحية تسجيل مركبة رحلات السفاري؟', 'يبقى تسجيل مركبة رحلات السفاري سارياً لمدة عام واحد أو حتى انتهاء الرخصة التجارية للشركة.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (219, 'هل يمكنني تغيير تفاصيل تسجيل مركبة رحلات السفاري؟', 'يمكنك فقط إضافة أو إزالة السائقين و/أو المركبات.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (220, 'هل سيكون تسجيل مركبة رحلات السفاري سارياً إذا انتهت صلاحية وثيقة تسجيلها الصادرة عن هيئة الطرق والمواصلات؟', 'لن يكون تسجيل مركبة رحلات السفاري سارياً إذا انتهت صلاحية وثيقة تسجيلها الصادرة عن هيئة الطرق والمواصلات. يتوجب عليك أن تعيد تسجيل مركبتك لدى هيئة الطرق والمواصلات وتحديث التسجيل الخاص بمركبات السفاري ليصبح سارياً.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (221, 'كيف يمكنني أن أجدّد وثيقة تسجيل مركبة رحلات السفاري؟', 'إذا انتهت صلاحية وثيقة تسجيل مركبة رحلات السفاري، فيتوجب عليك تقديم طلب جديد للحصول على وثيقة تسجيل أخرى.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (222, 'هل يمكنني إجراء تغييرات على تسجيل مركبتي في أي وقت؟', 'يمكن إجراء التغييرات حتى 24 ساعة قبل انتهاء صلاحية التسجيل.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (223, 'سوف تنتهي صلاحية تسجيل مركبات رحلات السفاري قريباً. هل سأتمكن من إجراء التعديلات وإضافة مركبات جديدة؟', 'نعم، يمكنك ذلك. لكن عندما تنتهي صلاحية تسجيل مركبات رحلات السفاري، تنتهي معه صلاحية تسجيل جميع مركباتك بما في ذلك المركبات التي أضفتها مؤخراً. ستحتاج حينها إلى تقديم طلب جديد لتسجيل مركبات السفاري وإضافة جميع المركبات مرة أخرى.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (224, 'هل يمكنني أن أوظّف سائقين بدوام حرّ مع مركباتهم وأن أضيفهم إلى وثيقة تسجيل مركبات رحلات السفاري الحالية؟', 'نعم، يمكنك إضافة سائقين بدوام حرّ مع مركباتهم إلى رخصة تسجيل مركبات رحلات السفاري سارية المفعول في أي وقت.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (225, 'من هو نظام iSupplier ولماذا يُستخدم؟', 'iSupplier هو نظام إلكتروني عبر الإنترنت مخصّص للبائعين والمورّدين حيث يتيح لهم التسجيل واستلام الاستفسارات مثل طلبات عروض الأسعار والمناقصات وطلبات توفير المعلومات وغيرها. تتيح اللوحة الرئيسية للبوابة الإلكترونية التواصل المباشر بين المورّدين وقسم المشتريات في دائرة الاقتصاد والسياحة بدبي والشركات، ويتم حفظ نسخة كاملة من المحادثات عبر البوابة. كما يسمح نظاما e-Sourcing و e-Invoice بحفظ طلبات المشتريات والفواتير المُقدّمة من المورّدين مع إمكانيّة الوصول إليها. يمكن للمورّدين كذلك تقديم ومتابعة الفواتير عبر الإنترنت، وتسلّم الدفعات عبر التحويلات الرقمية. تتميّز البوابة الإلكترونية أيضاً بنظام إشعارات خاص عبر البريد الإلكتروني يتيح للمورّدين البقاء على علم واطلاع بحالة الخدمات ذات الصلة.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (226, 'كيف يمكنني استلام المعلومات أو الاستفسارات؟', 'يستلم المورّدون الإشعارات حول الاستفسارات الجديدة مثل طلبات عروض الأسعار والمناقصات وطلبات توفير المعلومات، عبر البريد الإلكتروني. يمكن للمورّدين الوصول إلى المعلومات التفصيليّة عبر الإنترنت من خلال البوابة الإلكترونية.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (227, 'كيف يُمكنني إعادة ضبط اسم المستخدم الخاص بي أو كلمة المرور؟', 'يستطيع المورّدون إعادة ضبط كلمة المرور الخاصة بهم من واجهة تسجيل الدخول إلى نظامiSupplier أو عن طريق التواصل مباشرةً مع فريق المشتريات. تواصل معنا هنا.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (228, 'كيف يُمكنني تحديث اسم شركتي عبر البوابة الإلكترونية؟', 'على المورّدين تقديم رسالة رسميّة توضح الإسم الجديد للشركة. ستقوم دائرة الاقتصاد والسياحة بدبي بتحديث الاسم مباشرة عبر المنصّة الإلكترونية.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (229, 'هل يمكن أن يكون لشركتي أكثر من مستخدم تحت حساب واحد؟', 'نعم. كلّ شركة لديها حساب مستخدم رئيسي، وهذا الحساب يُمكنه إنشاء حسابات إضافية للمزيد من المستخدمين.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (230, 'من يحق له التسجيل على بوابة iSupplier الإلكترونية؟', 'جميع المورّدين الذين يحملون رخصة تجارية سارية المفعول يمكنهم إنشاء حساب على منصة iSupplier.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (231, 'هل يمكن للشركات من إمارات أو دول أخرى التسجيل على iSupplier؟', 'نعم، يمكن لأي شركة تمتلك رخصة تجارية سارية المفعول التسجيل.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (232, 'ما هي الوثائق المطلوبة للتسجيل كمورّد؟', 'على جميع المورّدين تقديم المستندات التالية وإرفاقها عند إكمال عمليّة التسجيل: الرخصة التجاريّة شهادة التأسيس نموذج عن ختم الشركة على خلفية بيضاء التفاصيل المصرفيّة في رسالة أو خطاب منفصل', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (233, 'ماذا لو كنت أمتلك شركة خارج دولة الإمارات وليس لدي الوثائق المطلوبة ولا يُمكنني تقديم جواز السفر أو صور عن بطاقات الهوية؟', 'إذا لم تتمكن من تقديم الوثائق أو المعلومات المطلوبة نيابة عن شركتك أو عن المستخدمين، عليك تقديم خطاب رسمي من الشركة يوضح إسم الشخص المفوّض بالتوقيع ونموذج عن التوقيع.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (234, 'ما هو الخطاب المطلوب من البنك، وما هي التفاصيل التي يجب أن يتضمّنها؟', 'يجب أن يحتوي الخطاب الصادر عن البنك على معلومات عن حساب الشركة المُسجّلة، بما في ذلك: إسم البنك، وإسم صاحب الحساب، ورقم الحساب، ورقم الـ IBAN، ورمز "السويفت"، والعنوان.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (235, 'هل يمكنني أن أقوم بالتسجيل شخصيّاً أو يجب أن يتم ذلك عبر البريد الإلكتروني؟', 'يجب أن تتم عملية التسجيل عبر الإنترنت. في حال واجهتك مشاكل أو احتجت لمزيد من المعلومات والتوضيحات، الرجاء التواصل معنا وسنكون سعداء بتقديم الدعم لك.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (236, 'هل يُمكن أن تنتهي صلاحيّة حساب iSupplier؟', 'نعم. سيتم إلغاء تفعيل الحساب في حال انتهاء صلاحية الرخصة التجارية المُسجّلة للشركة. سيستلم جميع المورّدين رسالة عبر البريد الإلكتروني قبل 30 يوماً من انتهاء صلاحية الوثائق المُسجلة. على جميع المورّدين تحديث بياناتهم ووثائقهم عبر البوابة الإلكترونية قبل موعد انتهاء الصلاحيّة لتجنب أي إيقاف للحساب.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (237, 'ما سيحدث في حال لم أقم بتحديث الوثائق الخاصّة بي، وانتهت صلاحيّة حسابي؟', 'سيتم إلغاء تفعيل الحساب ولا يمكن استخدامه للمشاركة في تقديم طلبات العروض أو المناقصات. عليك التواصل معنا وتقديم جميع الوثائق المطلوبة مرة أخرى ليتم تفعيل الحساب مجدّداً.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (238, 'هل يمكن تقديم الفواتير بشكل شخصي؟', 'لا، يجب تقديم جميع الفواتير عبر البوابة الإلكترونية.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (239, 'متى يجب أن أقدّم الفاتورة الخاصّة بي؟', 'عندما يتم إنهاء العمل أو الخدمة وتسليمها إلى الجهة المعنيّة، سيقوم العميل بطلب فاتورة من خلال البوابة الإلكترونية وسيستلم المورّد رسالة عبر الإلكتروني لتقديم الفاتورة من خلال نظام iSupplier.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (240, 'ما هي الوثائق المطلوبة لتقديم فاتورة؟', 'لتقديم فاتورة، على جميع المورّدين تقديم نسخة إلكترونية من الفاتورة مع مُذكّرة تسليم مُوقعة. في حال لم تتمكن من تقديم مُذكّرة تسليم مُوقعة، يمكنك تقديم بيانات الحضور أو تقرير الخدمات أو شهادة إتمام الخدمة، أو إرفاق رسالة عبر البريد الإلكتروني من دائرة الاقتصاد والسياحة بدبي، تفيد بعلم الدائرة بجميع التفاصيل المطلوبة.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (241, 'كيف يُمكنني متابعة دفعات الفواتير؟', 'لمتابعة المدفوعات المتعلّقة بأي فاتورة، الرجاء تسجيل الدخول إلى البوابة الإلكترونيّة عبر حسابك والبحث عن حالة الفاتورة عبر Invoice Catalog.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (242, 'لما تم رفض الفاتورة التي قدّمتها؟', 'يمكنك إيجاد المزيد من التفاصيل حول إحدى الفواتير، بما في ذلك أسباب الرفض، من خلال اختيار الفاتورة عبر Invoice Catalog ومن ثم الاطلاع على ملخص المدفوعات أو الفواتير.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (243, 'ما هي طرق الدفع المستخدمة من قِبل دائرة الاقتصاد والسياحة بدبي؟', 'تدفع الدائرة باستخدام التحويلات البنكية الرقمية، والشيكات المصرفيّة.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (244, 'كم من الوقت يتطلب إكمال دورة المرشد السياحي باللغة الإنجليزية؟', 'يعتمد ذلك على الشخص إلّا أنّها تستغرق عادةً بين 4 و6 أسابيع.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (245, 'ما هي مدة دورة المرشد السياحي بلغة الماندارين؟', 'تستغرق الدورة بلغة الماندارين 10 أيام.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (246, 'كيف يمكنني أن أطور قدراتي وأكون مُميّزاً لاجتياز اختبار المرشد السياحي بسهولة؟', 'من خلال صقل مهاراتك في تقديم العروض واكتساب المزيد من المعلومات عن المعالم السياحية والجوانب المعمارية والثقافية في دبي. وتتوفر أيضاً فرص رائعة للمرشحين متعددي اللغات لاسيّما من يجيدون اللغة اليابانية أو الكورية أو البرازيلية أو البرتغالية أو الروسية بالإضافة إلى الإنجليزية أو الماندارين.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (247, 'ما هي البرامج الدراسية التي يجب علي إكمالها؟', 'تشمل المقرّرات الدراسية عدداً من البرامج والدورات التدريبيّة بما فيها: نهج دبي، وأساسيات الإرشاد السياحي، ومهارات متقدمة في الإرشاد السياحي، والمرشد السياحي الخبير، ودورة حول كيفية التعامل مع المواقف الصعبة.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (248, 'ما هي اللغات التي يجب أن يتقنها المرشدون السياحيون حالياً؟', 'نحن نرحب بجميع المرشّحين الذين يمتلكون مهارات لغوية، لكننا نركز بشكل خاص على أولئك الذين يجيدون اللغة اليابانية أو الكورية أو البرتغالية أو الروسية إلى جانب الإنجليزية أو الماندارين.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (249, 'هل يمكنني تسديد رسوم المشاركة في دورة المرشد السياحي على دفعات؟', 'لا يمكنك الدفع بالتقسيط، لكن الدورة مقسمة إلى مراحل مختلفة، ويمكنك أن تدفع عندما تكون مستعداً للبدء في كل مرحلة.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (250, 'ماذا يحصل إذا انتهت صلاحية الترخيص الخاص بي؟', 'لا يمكنك مزاولة الإرشاد السياحي من دون ترخيص صالح حتى لو تقدّمت مسبقاً بطلبٍ لتجديده. في حال واصلت مزاولة الإرشاد السياحي بعد انتهاء صلاحية الترخيص، ستُفرض عليك غرامة ماليّة. وإذا لم تكن ترغب في استخدام ترخيصك بعد الآن، يجب عليك إلغاؤه لتجنب الغرامة.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (251, 'متى يجب أن أدفع غرامة في حال انتهت صلاحية ترخيصي؟', 'إذا واصلت العمل في مهنة الإرشاد السياحي بعد انتهاء صلاحية ترخيصك، سوف يُفرض عليك غرامة بقيمة 200 درهم إماراتي عن أول عامين بعد انتهاء صلاحيته، ثم تزداد قيمة الغرامة إلى 400 درهم إماراتي بعد فترةٍ تصل إلى 4 أعوام. في حال لم تدفع الغرامة في غضون أربعة أعوام، سوف يتمّ إلغاء ترخيصك. يجب عليك إلغاء ترخيصك لتجنب دفع غرامة.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (252, 'لقد أضعت ترخيصي، هل يمكنني الحصول على ترخيص جديد؟', 'احصل على "شهادة فقدان" من شرطة دبي وقمْ بتحميل تقرير الشرطة ونسخة عن الترخيص المفقود إلى حسابك على الموقع الإلكتروني وسوف نراجع الطلب ونرسل لك رخصة جديدة.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (253, 'ما هي المعارض التجارية المرتقبة التي ستشارك فيها دائرة الاقتصاد والسياحة بدبي؟', 'يمكنك الاطلاع على الجدول الكامل للفعاليات على الموقع الإلكتروني الخاص بنا.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (254, 'هل تشمل رسوم المشاركة تكاليف الطعام والواي فاي؟', 'تتوفر خدمات الطعام في أجنحة المعرض، أما بالنسبة للحصول على خدمة الواي فاي، فسيتم التعامل مع كلّ حالة على حِدة.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (255, 'هل يتم توفير أي دعم في مجال التسويق أو العروض الترويجية في حال المشاركة مع دبي للسياحة خلال الفعاليات؟', 'لا يتمّ توفير الدعم في مجال التسويق أو العروض الترويجية.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (256, 'ما هو عدد المشاركين الذين يمكن إدراجهم في كل طلب لإقامة فعالية تجارية؟', 'يختلف عدد المشاركين من فعالية لأخرى. سوف يقدّم لك فريقنا المشورة بمجرّد إتمام عملية التسجيل الخاصة بك.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (257, 'هل توفّرون أي دعم أو مساعدة للحصول على تأشيرة الدخول؟', 'نوفر للعارضين المُسجّلين خطاب دعوة ونسخة عن البطاقات التعريفيّة الخاصة بالفعالية، إذا لزم الأمر.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (258, 'ما هو الدعم الذي تقدمونه إذا كان لديّ أي طلبات خاصة؟', 'تسرّنا مساعدتك إذا كان لديك أي أسئلة أو استفسارات. يرجى التواصل معنا وذكر تفاصيل عن طلبك وسنجد طريقة مناسبة لتقديم المساعدة.', 2, null, '2022-10-21 21:59:06', '2022-10-21 21:59:06');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (259, 'Is the UAE and Dubai open for all tourists?', 'Effective from 30 August 2021, tourist visa applications are open to travellers from all countries. Depending on a passenger''s nationality, they can get a visa on arrival, or apply for a pre-arranged visit visa from Dubai Immigration before travel. Please reach out to The General Directorate of Residency and Foreigners Affairs and check your country’s latest travel advisory on outbound trips. Travellers are required to have either one of the three: A valid vaccination certificate with a QR code proving they are fully vaccinated with a vaccine approved by the WHO or the UAE A negative PCR test with a QR code, issued within 48 hours from the time of sample collection to the flight time by an approved health service provider  A valid medical certificate with a QR code issued by relevant authorities that shows they have recovered from COVID‑19 within a month prior to the date of arrival. Please check the following links for the most up-to-date requirements ahead of travelling to or transiting through Dubai: Emirates airline | FlyDubai', 1, null, '2022-10-21 21:59:07', '2022-10-21 21:59:07');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (260, 'Are there any movement restrictions for unvaccinated travellers in Dubai?', 'Unvaccinated travellers to Dubai need to present a negative PCR test result done within the last 48 hours or a certificate of recovery from COVID-19, obtained within one month before the date of travel. Printed or digital PCR test or vaccination certificates are accepted in English or Arabic and must include a QR code. SMS certificates are not accepted. Certificates in other languages are acceptable if they can be validated at the departure point. Unvaccinated visitors most likely have to present the same results or certificates when attending free movement events, such as standing concerts, depending on the venue they go to.', 1, null, '2022-10-21 21:59:07', '2022-10-21 21:59:07');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (261, 'Is it safe to travel to Dubai? What measures do you have in place?', 'Dubai is one of the world’s safest destinations –The UAE is #1globally in Bloomberg''s Covid Resilience Ranking (January 2022). The Ministry of Health and Prevention (MoHAP) alongside other relevant authorities continue to prioritise, monitor, and assure the safety and the well-being of all visitors to, and residents of Dubai as the utmost priority. Dubai Tourism continues to work diligently under the guidance from the World Health Organization to maintain rigorously high standards of public hygiene and safety, so the wellbeing of all residents and visitors remains uncompromised. Dubai has also been recognised by the World Travel & Tourism Council (WTTC) and received its Safe Travels stamp for its safety protocols in place.', 1, null, '2022-10-21 21:59:07', '2022-10-21 21:59:07');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (262, 'What are the international travel requirements when travelling to Dubai?', 'Effective from 26 February 2022, passengers travelling to Dubai from all countries (GCC included) must present one of the following: A valid vaccination certificate with a QR code proving they are fully vaccinated with a vaccine approved by the WHO or the UAE A negative PCR test with a QR code, based on a molecular diagnostic test intended for the qualitative detection of nucleic acid for SARS-COV-2 viral RNA, and issued within 48 hours from the time of sample collection to the flight time by an approved health service provider  A valid medical certificate with a QR code issued by relevant authorities that shows they have recovered from COVID‑19 within a month prior to the date of arrival. UAE nationals are exempt from the above, but will be required to take a PCR test on arrival at Dubai airport, unless the country of origin requires a pre-travel test. Some passengers may require a PCR test on arrival in Dubai and they must self-quarantine until receiving a negative result. If a passenger tests positive, they should follow the guidelines issued by the Dubai Health Authority. Children under the age of 12 and passengers with a severe or moderate disability are exempt from the PCR test. Please check the following links for the most up-to-date requirements ahead of travelling to or transiting through Dubai: Emirates airline | FlyDubai Printed or digital PCR or vaccination certificates are accepted in English or Arabic and must include a QR code. SMS certificates are not accepted. PCR or vaccination certificates in other languages are acceptable if they can be validated at the departure point (the date and time of the test must be detailed). Tourists may test at any lab accredited by their health authority in their country. If you are travelling with Emirates, please click here for a list of accredited labs. Ensure you have medical travel insurance with international coverage that covers COVID-19 before travelling. Customers purchasing an Emirates airline or FlyDubai ticket from 1 December 2020 benefit from additional multi-risk travel insurance provided by AIG Travel or NEXtCARE through to 31 March 2022, including cover for COVID-19 and overseas medical expenses. Please see the following links for more information and a supporting FAQ: Emirates airline | FlyDubai Fulfil entry visa requirements to visit the UAE. Transit passengers are asked to comply with entry protocols of their final destination.  ', 1, null, '2022-10-21 21:59:07', '2022-10-21 21:59:07');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (263, 'Do I need travel insurance when coming to Dubai?', 'Yes, ensure you have medical travel insurance with international coverage that covers COVID-19 before travelling. Customers purchasing an Emirates airline or FlyDubai ticket from 1 December 2020 benefit from additional multi-risk travel insurance provided by AIG Travel or NEXtCARE through to 31 March 2022, including cover for COVID-19 and overseas medical expenses. Please see the following links for more information and a supporting FAQ: Emirates airline | FlyDubai', 1, null, '2022-10-21 21:59:07', '2022-10-21 21:59:07');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (264, 'What are the procedures from my departure destination ahead of travelling to Dubai?', 'Please check your country’s travel advisory for latest guidance on outbound trips, as well as your carrier requirements. Fulfil entry visa requirements to visit the UAE.', 1, null, '2022-10-21 21:59:07', '2022-10-21 21:59:07');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (265, 'Following arrival, what are the procedures for exiting Dubai Airports?', 'Tourists who show symptoms on arrival and test positive for COVID-19 after being re-tested must follow the guidelines issued by the Dubai Health Authority. The traveller will bear the cost of treatment and quarantine unless their carrier is Emirates or FlyDubai. For those that are a COVID-19 close contact case, no quarantine is required if you are not experiencing any symptoms. Customers purchasing an Emirates airline or FlyDubai ticket from 1 December 2020 benefit from additional multi-risk travel insurance provided by AIG Travel or NEXtCARE through to 31 March 2022, including cover for COVID-19 and overseas medical expenses. Please see the following links for more information and a supporting FAQ: Emirates airline | FlyDubai Compliance is required with all precautionary measures in Dubai (wearing masks at public indoor venues and adhering to frequent hand sanitisation).', 1, null, '2022-10-21 21:59:07', '2022-10-21 21:59:07');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (266, 'Do I need a COVID-19 test when travelling to Dubai?', 'Effective from 26 February 2022, tourists and residents arriving into Dubai from any point of origin (GCC included) must present one of the following: A valid vaccination certificate with a QR code proving they are fully vaccinated with a vaccine approved by the WHO or the UAE A negative PCR test with a QR code, issued within 48 hours from the time of sample collection to the flight time by an approved health service provider  A valid medical certificate with a QR code issued by relevant authorities that shows they have recovered from COVID‑19 within a month prior to the date of arrival. Some passengers may require a PCR test on arrival in Dubai and they must self-quarantine until receiving a negative result. If a passenger tests positive, they should follow the guidelines issued by the Dubai Health Authority. UAE nationals are exempt from the above, but will be required to take a PCR test on arrival at Dubai airport, unless the country of origin requires a pre-travel test. Printed or digital PCR or vaccination certificates are accepted in English or Arabic and must include a QR code. SMS certificates are not accepted. PCR or vaccination certificates in other languages are acceptable if they can be validated at the departure point (the date and time of the test must be detailed). Tourists may test at any lab accredited by their health authority in their country. If you are travelling with Emirates, please click here for a list of accredited labs.', 1, null, '2022-10-21 21:59:07', '2022-10-21 21:59:07');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (267, 'Will children and newborns be subject to COVID-19 PCR tests as well?', 'All children below 12 years of age are exempt from the COVID-19 PCR test.', 1, null, '2022-10-21 21:59:07', '2022-10-21 21:59:07');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (268, 'Is there going to be a mandatory quarantine for tourists?', 'No, only if they test positive with COVID-19. Please check carrier and Dubai travel requirements for COVID-19 PCR testing. Passengers who need to get a COVID-19 PCR test done at arrival in Dubai should self-quarantine until they receive their results, which is expected within 12 hours.', 1, null, '2022-10-21 21:59:07', '2022-10-21 21:59:07');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (269, 'If I show symptoms on arrival at Dubai Airport and test positive, what happens?', 'If the result is positive, compliance with 10-day isolation becomes mandatory. A person who is infected with COVID-19 will remain in isolation for a duration of 10 days (home or facility). The isolation ends after completing the 10 days with improvement in symptoms and no fever for at least three consecutive days, without any fever reduction medication. For more information, please visit this Dubai Health Authority page. For those that are a COVID-19 close contact case, no quarantine is required if you are not experiencing any symptoms.', 1, null, '2022-10-21 21:59:07', '2022-10-21 21:59:07');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (270, 'Are transit travellers required to follow the same procedures related to conducting Covid-19 PCR tests?', 'For transit passengers, the rules and conditions for entry at the final destination will apply. Passengers must check their country’s travel advisory for latest guidance on outbound trips, as well as the carrier requirements.', 1, null, '2022-10-21 21:59:07', '2022-10-21 21:59:07');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (271, 'What are the procedures when departing from Dubai?', 'Passengers departing Dubai Airports will need to do a PCR test only if it is mandated by the country they are travelling to. A rapid PCR or Rapid Antigen test will be available at the airport to travellers departing from Dubai if their destination country requires it.  Passengers are advised to check the travel protocols of their carrier and arrival destination. Note: Airlines have the right to deny travellers if they display any symptoms of COVID-19.', 1, null, '2022-10-21 21:59:07', '2022-10-21 21:59:07');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (272, 'What do I need to know or what are the rules I have to follow when in Dubai?', 'Responsibilities of travellers: Adhere to all official measures and requirements established by the Dubai Government and other countries before travelling and after returning to Dubai. Disclose any symptoms before travelling by filling in the form provided by airlines. Tourists will bear the costs of examination and treatment abroad, in the event they are infected with COVID-19, so are advised to have international health insurance coverage that covers COVID-19 before travelling. Take precautionary measures and self-monitor for any symptoms of COVID-19 Follow the isolation guidelines in the case you show symptoms on arrival and the result is “positive”.', 1, null, '2022-10-21 21:59:07', '2022-10-21 21:59:07');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (273, 'Do I have to wear a mask during my holiday in Dubai?', 'Masks are compulsory in public indoor venues, while wearing one in outdoor areas is optional. Additionally, you must continue to sanitise your hands frequently.', 1, null, '2022-10-21 21:59:07', '2022-10-21 21:59:07');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (274, 'Do tourists have to pay for the treatment and quarantine stay in a hotel if they show symptoms and/or require a second test and the test is positive?', 'The traveller will bear the cost of treatment and quarantine. Customers purchasing an Emirates airline or FlyDubai ticket from 1 December 2020 benefit from additional multi-risk travel insurance provided by AIG Travel or NEXtCARE through to 31 March 2022, including cover for COVID-19 and overseas medical expenses. Please see the following links for more information and a supporting FAQ: Emirates airline | FlyDubai Alternatives for isolation include institutional quarantine (dedicated hotels/facilities), home or hospitalisation and potential options based on the patient’s assessment. A person who is infected with COVID-19 will remain in isolation for 10 days (home or facility). The isolation ends after completing the 10 days with improvement in symptoms and no fever for at least three consecutive days, without any fever reduction medication. For more information, please visit this Dubai Health Authority page. For those that are a COVID-19 close contact case, no quarantine is required if you are not experiencing any symptoms. The traveller will bear the cost of treatment and quarantine unless their carrier is Emirates airline or FlyDubai.', 1, null, '2022-10-21 21:59:07', '2022-10-21 21:59:07');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (275, 'If tourists who are spending their stay at family’s, friend’s residences and show COVID-19 symptoms on arrival and require a second test, would they be required to go into quarantine?', 'If the COVID-19 test result is positive, the traveller is required to adhere to the isolation guidelines. The location for isolation will be determined according to the household situation and the health status of the patient (institutional isolation or at the hospital). A person who is infected with COVID-19 will self isolate. The isolation period for a confirmed COVID-19 case is 10 days. The isolation ends after completing the 10 days with improvement in symptoms and no fever for 3 consecutive days at least, without any fever reduction medication. For more information, please visit this Dubai Health Authority page. For those that are a COVID-19 close contact case, no quarantine is required if you are not experiencing any symptoms.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (276, 'Do I need a visa and if yes, how do I apply?', 'For all the visa related information please reach out to The General Directorate of Residency and Foreigners Affairs and check your country’s travel advisory for latest guidance on outbound trips.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (277, 'Are the Emirates airline flight routes fully restored?', 'Please click here for information on which destinations Emirates is flying to and from.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (278, 'Have MICE events resumed?', 'Yes, local and international MICE events have resumed as well as leisure & retail events. All events are following stringent safety and security protocols. Business events can operate at a reduced capacity of 70% while adhering to all government-issued protocols. Institutional events and award ceremonies can take place with a maximum of 1,000 people, with no mandatory vaccination requirements.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (279, 'Have live events resumed in Dubai?', 'Entertainment activities including classical, theatrical and comedy events can resume upon securing a permit, with event organisers and attendees adhering to the latest safety and precautionary measures. For more information please contact the Dubai Tourism Call Centre Tel: 600 555 559. Mass, entertainment, sporting and business events (including audiences) must adhere to an event capacity of 70% and must follow social distancing guidelines.  Social activities can increase capacity to 60% and not exceed 300 people.  For event vaccination requirements, please refer to latest published protocols.  ', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (280, 'Are destination weddings able to be planned in Dubai?', 'Yes, weddings can be planned and hosted in Dubai with an increased capacity to 60%, not exceeding 300 attendees. Elderly people and those with chronic conditions are advised not to attend gatherings.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (281, 'What are the dates for Expo 2020 and where can I get further details?', 'The global mega event launched on 1 October and will run through to 31 March 2022. For the latest updates please visit https://www.expo2020dubai.com/ or @Expo2020dubai on social media.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (282, 'Do I need to be vaccinated to visit Expo 2020?', 'All visitors must show proof of at least one dose of any COVID-19 vaccine, recognised by any Expo 2020 participating country or the World Health Organisation (WHO). If unvaccinated, visitors must present a negative PCR test certificate for a test conducted within the last 72 hours. For further information on Expo 2020’s COVID-19 safety measures, please click here.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (283, 'Where can I find the official measures and requirements by the Dubai Government?', 'The public are required to follow precautionary measures, which include: Wearing a face mask in all public indoor venues. Adhering to frequent hand sanitisation. Please find below helpful links to official sources: Dubai Airports website Dubai Municipality website Dubai Health Authority website Federal Authority for Identity and Citizenship website General Directorate of Residency and Foreigners Affairs-Dubai website United Arab Emirates Ministry of Health & Prevention website', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (284, 'When do I need an activity permit?', 'You need an activity permit if you are a venue such as a hotel, mall or restaurant or any venue wanting to organise recurring entertainment or activities, such as a musician, a band, shows or a temporary play area.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (285, 'What happens if my activity permit is rejected?', 'You can discuss your case by contacting the DET e-Permit department via the e-Permit portal. All concerns will be resolved on a case-by-case basis.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (286, 'How soon will I get my activity permit after it''s approved?', 'Upon approval, your activity permit will be issued within one business day.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (287, 'How long can an activity continue for?', 'Activities can last from one day to three months.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (288, 'Who can apply for this service?', 'Only hotel operators can apply for a tent permit.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (289, 'When can I apply for a temporary tent permit?', 'You can apply for a tent permit up to 24 hours before the day of the proposed event.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (290, 'Can I apply for an entertainment event permit in the entertainment tent?', 'Yes, as soon as the entertainment tent permit is issued, the tent will be added under the sub-venue listing of the hotel. You can apply for the entertainment event permit by selecting it from this list.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (291, 'Can I amend my request for entertainment tent permit?', 'No, but you can apply for a new one.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (292, 'Can I extend my request for entertainment tent permit?', 'You can apply as a new permit and include your new dates.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (293, 'Can I add a new activity type to an issued permit?', 'Yes, this is possible for entertainment activity permits.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (294, 'How many times can I amend the activity permit?', 'There is no limit to the amendments. Some amendments need to be conducted before the activity starts, while others can be done when the activity is still ongoing.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (295, 'Is there any fee for this service?', 'No, it’s free of charge.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (296, 'What are the types of private activities?', 'Events of a personal nature like birthdays, engagements and weddings. Some corporate events also fall under private activities, such as staff meetings (as long as the attendees are staff members in the same organisation).', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (297, 'How long does it take to get an activity amendment approved?', 'On approval, your permit will be amended within one business day.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (298, 'When should I apply for private activity?', 'Once you have secured your venue you can apply for a private activity permit. Applications can be received up until 24 hours before the event commences.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (299, 'What makes DCT different?', 'Dubai College of Tourism (DCT) is a government-backed college established by Department to Economy and Tourism in Dubai. Our curriculum is designed to embed the life skills and confidence graduates need to build exciting careers in the tourism industry. We deliver this through a platform of practical and vocational training, including internships, site visits, workshops and collaborative projects - taught by teachers with direct industry experience and with special insights from guest lecturers from the very best of homegrown and international industry leaders. A fully education delivered from one of the world''s most pioneering tourism brands.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (300, 'Are the DCT courses accredited?', 'Yes. Our courses are approved by the Knowledge and Human Development Authority (KHDA). Dubai College of Tourism also has global accreditation with the Institute of Hospitality for its programmes in Tourism, Events, Hospitality and Culinary Arts.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (301, 'Is IELTS or TOEFL certification necessary to apply?', 'International students do require either IELTS or TOEFL certification. UAE residents are required to pass our English Aptitude Assessment.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (302, 'When is the intake?', 'Admissions are for a September intake. Please apply before then.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (303, 'Can I visit the college?', 'Yes. We have formal open days, but you can request a visit at any time. We look forward to welcoming you and showing you around. You can contact us at info@dct.ac.ae to arrange a visit.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (304, 'What is the cost?', 'The latest pricing schedule can be found on the Dubai College of Tourism website.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (305, 'Can I pay in instalments?', 'Residents of the UAE have the option to pay in instalments.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (306, 'Do I have to pay to apply?', 'No, applications are free.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (307, 'What prerequisites must be in place if I wish to apply for a desert camp permit?', 'To apply for the desert permit, you have to have the following: A valid safari permit with 10 vehicles owned and registered under the company, or five vehicles plus a bus with a minimum of 45 seats registered to the company. Minimum five tour guides at the company. Approval from the department of Civil Defense. Approval from Dubai Municipality.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (308, 'Can I apply for a desert camp permit with freelance drivers or tour guides?', 'No, safari drivers and tour guides must be under the company visa.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (309, 'How long is a desert camp permit valid for?', 'A safari permit is valid for one year or until the expiry of the tenancy contract.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (310, 'How can I renew my desert camp permit?', 'If your permit has expired, you have to apply for a new permit.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (311, 'Can I amend desert camp permit to extend its area?', 'Yes, you can as long as you have all of the necessary and updated documentation in place.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (312, 'How many tour guides I can add to my permit?', 'A minimum of five tour guides are needed to be issued a permit. You can add as many tour guides as you wish above the minimum of five.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (313, 'How can I amend or add new vehicle to my desert camp permit?', 'When you amend your permit you can change these details.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (314, 'Who can apply for a tour operator desk permit?', 'Tour operators and tourism companies.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (315, 'How long does it take to apply and get approved for a tour operator counter or kiosk?', 'It can take up to 24 hours to receive an approval for a tourism counter or kiosk.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (316, 'Can I amend or renew my counter permit?', 'No, you need to apply for new one after expiry.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (317, 'Are there any specifications for the required travel desk photos?', 'Yes, the photos must be clear, and show the counter from two different angles.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (318, 'What is Dubai Assured?', 'Dubai Assured is a compliance programme to certify and recognise hotels, retail, F&B, and attractions that have implemented all public health protocols for the prevention and management of COVID-19, as part of the efforts to ensure that Dubai is at its highest level of safety preparedness ahead of the city’s reopening to international visitors. The stamp is a visual identity that guests will recognise throughout their stay in Dubai, reinforcing the message that the establishment is strictly adhering to health and safety protocols.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (319, 'Why is Dubai Assured important?', 'The quality of health and safety measures is on the minds of travellers when selecting their destination of choice, and the Dubai Assured stamp for the hospitality sector is a critical mechanism designed to assure tourists of a smooth and safe experience at every stage of their travel journey, from arrival at our airports to the point of departure from Dubai.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (320, 'Which authority introduced Dubai Assured?', 'Dubai Assured is a collaboration between the Department of Economy and Tourism (DET) and Dubai Municipality.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (321, 'Which establishments can participate?', 'All establishments within the hotel, retail, F&B, attractions, and leisure & entertainment sectors are highly encouraged to comply with the authorities’ health and safety guidelines to prevent the spread of COVID-19. The Dubai Assured stamp will showcase each establishment’s strict adherence to health and safety, thus instilling confidence in and enhancing, the guest experience.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (322, 'How can an establishment become Dubai Assured-certified?', 'Establishments should strictly adhere to the health and safety guidelines issued by Department of Economy and Tourism. Certification will be issued as part of the authorities’ regular 15-day inspection cycle. A digital toolkit with the Dubai Assured logo and associated brand guidelines will be shared with all compliant establishments so that they can display the certification on their premises. ', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (323, 'How long is the Dubai Assured stamp valid for after inspection?', 'The specially designed Dubai Assured stamp will be issued free of charge and will be valid for 15 days. It will be renewed every two weeks upon further verification by the relevant inspectors.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (324, 'What health and safety measures does an establishment need to meet in order to get the Dubai Assured stamp?', 'Detailed guidelines designed for each sector can be obtained from Department of Economy and Tourism. In addition to the standard precautionary measures of physical distancing and wearing of masks at all times by guests and employees, specific measures have been undertaken as examples. Hotels in Dubai have also deployed stringent disinfection measures of their facilities, as well as enhanced sanitisation covering high-frequency contact points in lobbies and other areas including restaurants and swimming pools. Entrances to hotels have also been fitted with equipment for contactless screening of guests.  Mall operators are also using advanced technology to minimise risks including use of thermal scanners and cameras to monitor pedestrian traffic at all entry points.  Public parks and beaches under Dubai Municipality have reopened with strict guidelines to be followed by visitors to these facilities. Restaurants are permitted to serve guests with reduced seating arrangements and maintain a distance of two metres between tables.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (325, 'Is there a Dubai Assured website?', 'Yes, please visit www.DubaiAssured.ae for more details.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (326, 'Are there any events that you can’t list on Dubai Calendar?', 'You can not list the following event types on Dubai Calendar: Private functions or closed invitation events Branded product launches or in-store promotions Events selling art, retail or commercial products Any auditions, workshops, courses or classes Individual school events, programmes or holiday camps Events of personal benefit, such as weddings, graduations, reunions, anniversaries, birthdays, etc Events for political organisations whose primary purpose is to influence legislation Events that promote alcohol, firearms, weapons, pornography, gambling/lottery, political issues and comparative sponsorship', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (327, 'Do I have to have an e-ticketing permit to list my event on Dubai calendar?', 'You don’t need an e-ticketing permit to list your event on Dubai Calendar. However, you do need an event permit.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (328, 'How can I modify/remove my event listing on Dubai Calendar?', 'If you would like to change your event listing, please contact us.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (329, 'How long does it take to have my event appear on the Dubai Calendar?', 'Your event will be listed within two business days if all your documentation is complete.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (330, 'Can I get financial support for my event?', 'Yes. As DET, we offer sponsorship opportunities. Read more about sponsorship options', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (331, 'Where can I register for Dubai Expert?', 'Please visit www.dubaiexpert.ae  ', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (332, 'Who is eligible to become a Dubai Expert?', 'Only travel agents and tour operators are valid to become a Dubai Expert.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (333, 'What do I need to do become a Dubai Expert?', 'To become a Dubai Expert, you are required to: - Achieve a Perfect score on a minimum of 17 missions - Watch all 25 videos embedded in the game', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (334, 'How long should it take me to complete Dubai Expert?', 'We recommend spreading your Dubai Expert journey out over a few days to become fully immersed in it, and benefit from greater recall.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (335, 'How long will it take to become a Dubai Expert?', 'To become a Dubai Expert requires the completion of 17 missions, 25 videos and 50 timed questions. It could be completed in a day, but we suggest to ‘play’ over time.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (336, 'What sort of results can I get in Dubai Expert?', 'For each mission you play, you can get one of three results. Perfect: the player managed to achieve a score that is high enough to unlock all the three rewards embedded in a mission. Completed: the player successfully completed a mission but didn''t manage to achieve a high enough score. Failed: A mission is failed when the Traveller Satisfaction gauge falls to zero.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (337, 'Is there an additional level that I can progress to in Dubai Expert?', 'Yes. You can become a Dubai Expert Plus by meeting the following criteria: - Perfect scores in 27 missions - Watch all 25 videos - Answer all 55 timed questions correctly', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (338, 'What are the technical requirements for playing Dubai Expert?', 'Dubai Expert works best on Chrome or Firefox browsers. The Dubai Expert game will take two to five minutes to download, during which time the browser must be kept open. Navigation away from the page will pause game download. Any use of a proxy or VPN may disrupt Dubai Expert.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (339, 'Does Dubai Expert work on all web browsers?', 'For optimal performance, we suggest that the Dubai Expert game is played using Chrome or Firefox browsers.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (340, 'Is there a Dubai Expert App?', 'Yes. The Dubai Expert is available on both the Apple''s App store and Google''s Play Store.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (341, 'What is Dubai Way?', 'Dubai Way is the training and engagement platform for anyone who interacts with tourists as part of their job. Having the correct knowledge and information about Dubai, it’s history, culture and heritage and excellent customer service skills are critical to delivering exceptional experiences for visitors to Dubai.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (342, 'How does Dubai Way work?', 'The Dubai Way training is completely online. It is video based learning with interactive activities and assessments throughout to verify your knowledge.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (343, 'How much does using Dubai Way cost?', 'Your employer will be responsible for the cost of the Dubai Way and you should register for the Dubai Way via your employer who will provide you with their establishment code.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (344, 'How can I register?', 'If you are resident and working in Dubai, you can register for Dubai Way via your employer. Your employer should be a registered establishment with Dubai Way and they will be able to provide you with an establishment code. Once you have the establishment code, you can register using your Emirates ID and progress to the Dubai Way training platform.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (345, 'I am stuck. What can I do?', 'There is a live chat assistant available 24 hours a day to assist you with your learning. Please click onto the Need Help button.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (346, 'How long will it take me to complete the Dubai Way?', 'The course can take anything from a few days to a few weeks, depending on how often you study. Once you commence the Dubai Way, you have 90 days to complete the course before the training will time out.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (347, 'Is Dubai Way translated into different languages?', 'Level 2 of Dubai Way is taught in English with subtitles provided in Arabic and Mandarin languages. Level 1 of Dubai Way has both voiceover and subtitles in English, Arabic, Hindi, Urdu, and Bengali', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (348, 'When I finish the Dubai Way, what will I receive?', 'As a graduate of the Dubai Way, you will be recognized as a Dubai Way champion and will receive a certificate and pin in recognition of your achievement.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (349, 'Why is completing the Dubai Way important?', 'Tourism is a major contributor to Dubai’s GDP and the reason why we all have job opportunities. With more knowledge and information you will be better prepared to welcome tourists and to provide the very best tourism experience. The better the tourism experience, the more visitors will want to return and the more opportunities for all of us.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (350, 'What types of events do I need a permit for?', 'Entertainment, sport, business, religious and charity events are all event types that require permits issued through e-Permit portal.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (351, 'What documents are required for registration?', 'For registration purposes, you are only required to submit: A copy of trade license, The event coordinator Passport copy (venue account), An NOC for Registration and the Emirates ID for the system user. A maximum file size of 10 MB is permitted and .html, .htm, .aspx, .exe,.xhtml and .dll files are not accepted.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (352, 'If a trade licence is expired, can I register in the system?', 'Your trade license must be valid for a minimum of four weeks prior to the event start date. The e-permit system will automatically send a notification to renew your licence three months before expiry.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (353, 'Can any event organiser register to run any type of event?', 'To apply for an event, a company trade license should clearly state one of the following activities for the e-Permits registration: Entertainment events: Parties and entertainment services Business events: Conference and exhibition organisation Sport events: Rallies and sport tournament organising services, sports exhibition organising or sports services or event management. A free zone company license should clearly state ‘Event management’ activity in order to register on the e-Permits portal. Additionally, you can apply for events permits within free-zone areas.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (354, 'How long will it take to approve my account?', 'It will be approved within 24 hours.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (355, 'How soon will I get my event permit after it''s approved?', 'Upon approval, your activity permit will be issued within five business days after receipt of payment of fees.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (356, 'What happens if my event permit is rejected?', 'You can discuss your case by contacting the DET e-Permit department via the e-Permit portal.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (357, 'Can I add a new event type to an issued permit?', 'No, you will need need to apply for a new permit for the additional event type.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (358, 'How many times can I amend the e-permit?', 'There is no limit to the amendments involved, given that the event is still at least one working day away.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (359, 'How long does it take for a change to be approved and reflect on my permit?', 'On approval, your permit will be amended within 24 hours.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (360, 'Can freezone-registered companies run events across all of Dubai?', 'Freezone companies can only conduct events within freezones. Companies licensed in Dubai can conduct events on both Dubai mainland and freezones.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (361, 'If company holds a trade license from other emirates (i.e. RAK Free zone) would this be accepted to use e-Permits?', 'No. Only companies holding a Dubai trade license can use the e-Permits.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (362, 'Who can apply for the Leisure Event Sponsorship Scheme?', 'Event Organisers who are hosting an event that is: - Ticketed - Open to the public - Eligible to apply for or holds a Leisure Event Permit', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (363, 'What happens if my application is rejected?', 'Please note that you can apply again if your event is rejected, provided it is still within three months of the event start date. ', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (364, 'When can I apply for the Leisure Events Sponsorship Scheme?', 'You can apply for the scheme effective immediately. For details, please visit www.visitdubai.com/en/leisure-events-sponsorship.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (365, 'What is the minimum timeframe for submitting my application for the Leisure Events Sponsorship Scheme?', 'Applications must be received a minimum of four (4) weeks prior to the event date.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (366, 'Do I need an event permit in order to apply for the Leisure Event Sponsorship Scheme?', 'You must be eligible for a Leisure Event Permit in order to apply for sponsorship. If approved for funding support, you will be required to have an approved event permit to formalise the contract.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (367, 'My event permit has been applied for by the venue or ticketing provider. Can they apply for the Leisure Event Sponsorship Scheme on my behalf?', 'No, only the event organiser who has contracted the event/artists may apply for sponsorship.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (368, 'I am both the venue and organiser of the event. Can I apply for the Leisure Event Sponsorship Scheme?', 'Yes, however you will need to provide evidence that you have contracted the event/artists directly as part of formalising the contract.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (369, 'How quickly will I receive a response on my application?', 'Within five working days.', 1, null, '2022-10-21 21:59:08', '2022-10-21 21:59:08');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (370, 'How do I apply for a Leisure Event Permit?', 'Please follow this guide to help you apply for a permit.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (371, 'How will DET confirm that I am the event organiser?', 'In assessing the application we may request evidence that you hold the contract for the event/artist etc.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (372, 'If I hold an Activity Event Permit, am I eligible to apply for sponsorship?', 'Only holders of Leisure Event Permits for a ticketed, public event can apply for sponsorship.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (373, 'Once I receive approval on my application, what are the next steps?', 'Follow the steps below: Step 1: If you have not already done so, apply for your event permit and ticketing links Step 2: You should ensure you are registered as a DET Supplier Step 3: Agree event benefits with your Leisure Events team Account Manager Step 4: Add Dubai Calendar as a ticketing channel on all event collateral, website, advertising material etc. Step 5: Finalise your contract with DET, including a copy of your trade licence Step 6: Market and hold your event ensuring agreed benefits are realised Step 7: Work with your contracted ticketing company to reconcile ticketing report and payments with DET Step 8: Upon receipt of LPO for sponsorship amount from DET, issue an invoice as instructed for sponsorship payment', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (374, 'Can I receive funding upfront?', 'No, the sponsorship will be paid within three (3) working days of financial reconciliation (funds cleared) of the ticketing fee for the event with DET.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (375, 'Am I still eligible for sponsorship if I have a signed contract and my event is postponed to another date?', 'Yes, however you must formally notify your account manager of the date change and an addendum will be added to the existing contract.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (376, 'What happens if I have a signed sponsorship contract and my event is cancelled?', 'Your contract will be cancelled, funding is only payable upon event closure with DET.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (377, 'If I am the venue and the organiser (co-partnership with the international/local organiser) do I get the sponsorship too?', 'Only one party can apply for sponsorship per event. If there is any doubt, we will request a signed statement from the contracting party stating they have the rights to act on behalf of and contract for the event.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (378, 'What is a ticketing API partner?', 'An API partner refers to ticketing/registration companies integrated with DET in order to sell tickets/take registrations for events held in the emirate of Dubai. A technical integration must take place to link the ticket seller’s system with DET’s system ensuring compliance with the current regulations.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (379, 'Is my company eligible to become a ticketing API partner', 'Any new or existing company in possession of a ticketing/registration system and holding a valid Dubai Trade Licence is eligible to becoming an Authorised Ticket Seller upon integration.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (380, 'What are the requirements for becoming a ticketing API partner?', 'To get API access you will need: - Valid Dubai Trade License - Trade Licence activities related to events industry - Operational Ticketing/Registration system - Signed API Ticketing Agreement (provided by DET) - Submit the API Application Form', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (381, 'What are the fees to becoming a ticketing API partner?', 'The integration attracts a one-time, non-refundable integration fee of 10,000AED.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (382, 'How long does it take to set up an API?', 'The integration process takes anywhere between two to eight weeks depending on the responsiveness of your technical team.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (383, 'What is eTix?', 'Available via our online platform, the eForm eTix Dubai facilitates electronic submission of NENs by event organisers. Please note that hard copy (paper) NENs are obsolete and no longer accepted by DET.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (384, 'How long does it take to activate my eTix account?', 'Accounts will be activated within one business day once the email address is validated. If you do not receive a validation email, please contact us.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (385, 'Can my company have multiple users on the eTix system?', 'An events company can have multiple users on the system who can complete and submit NENs on its behalf. For each user the company must provide an official NOC with the applicant''s full name and Emirates ID number. Each user must also register independently using a unique email address and attach their Emirates ID copy and NOC.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (386, 'What type of amendments can I make for my event on eTix?', 'You can add or change dates, participants and venue.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (387, 'What is the walk-in ticketing system?', 'The system replaces the previous manual process of stamping entry tickets for entertainment venues. The new system offers faster ticketing authorisation and allows event managers and venues to issue tickets immediately.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (388, 'How does the walk-in ticketing system work?', 'After successfully registering a ''super user'' account, you can create additional users, set ticket categories and prices, and format the ticket design.The venue will need to have a working ticket printer (a list of supported tickets and printers is provided). DET fees are automatically calculated with an electronic invoice generated on a monthly basis and payable online.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (389, 'How do I start printing walk-in tickets?', 'Purchase a ticket printer and blank tickets from an approved supplier and finalise your ticket design. Then, set up an admin account using the online New Registration form on the walk-in ticketing platform. To activate your account, go to the DET offices with your approved email notification or application reference number, required authorisation documents, PRO card or a formal authorisation letter naming you as the company system administrator, and Emirates ID copy.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (390, 'Can I use my own branded tickets for walk-in?', 'Yes, as long as you follow the guidelines provided in the template design. Your design should be submitted online for final DET approval.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (391, 'Can you sell venue walk-in tickets online?', 'The system does not support online ticket sales as this is classed as an entry fee. Tickets may be sold online via approved ticket sales partners and exchanged at the door for a venue walk-in ticket.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (392, 'Is it possible to reprint/return/refund walk-in tickets?', 'No. Each printed ticket will be subject to the standard DET 10% fee calculation.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (393, 'What is a holiday home?', 'A holiday home is any furnished mainland or freezone-located residence (studio to multiple bedrooms) that is rented out to guests based on the activity terms and criteria.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (394, 'Are hotel or hotel apartment units included as holiday homes?', 'No. Hotel and hotel apartments have a different classification. Discover the hotel classification service here', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (395, 'How long will it take to approve my holiday home registration?', 'Once registration is reviewed and approved, individual owners will be sent a payment link within one business day. The registration will become active once payment is confirmed. Professional operators accounts will be active within one business day of registration.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (396, 'Can I register a holiday homes under a real estate or inbound tourism operator licence?', 'No. Your trade licence must have Vacation Homes Rental as the designated activity.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (397, 'What is the holiday home registration validity period?', 'There is no expiry date, however you will need to annually update the trade licence and any other relevant information. There is no renewal fee applicable.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (398, 'As a tenant, do I need Ejari documentation for my tenancy contact?', 'Yes, you require a tenancy contract approved by Ejari.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (399, 'I recently changed my DEWA account and I don’t have a recent bill.', 'You can either submit a statement of account from DEWA or log in to your DEWA account and take a screenshot of the account profile. The screenshot must show the account owner and DEWA account number.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (400, 'What should I provide if I do not have a title deed?', 'The Sales and Purchase Agreement (SPA) and either a payment completion certificate or a letter (Addressed: To Whom It May Concern) from the developer or building owner(s) clarifying the below: 1. That there is no title deed for this unit 2. If the unit is still on a payment plan, mention that the unit is on a payment plan and there is no outstanding dues 3. Mention that the unit is residential', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (401, 'Are all villas eligible for holiday home permits?', 'No. Residential villas must be in a gated compound comprising a minimum of four villas. Commercial villas located on main roads may also be considered by DET to operate as holiday homes.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (402, 'What is the validity period for a holiday homes permit', 'A holiday homes permit is issued for a minimum of three months and maximum of 12 months. Permit fees are irrespective of validity period.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (403, 'My permit has expired and been removed from the system. How can I retrieve it?', 'If your permit has expired then it your unit has been permanently deleted in the system. Your next step is to reapply as a new unit.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (404, 'When can I apply to renew my unit permit?', 'The renewal icon will appear 30 calendar days prior to expiration of your current permit. Failure to renew will result in your unit being removed from the system and you will have to reapply as a new unit.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (405, 'Why do I need to cancel my holiday home permit?', 'If your unit is listed as a holiday home but no longer operating as such then the Holiday Homes permit regulations and obligations are no longer applicable and your permit should be cancelled.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (406, 'Are there any other admin requirements when cancelling my permit?', 'Any and all outstanding Tourism Dirham fees must be settled.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (407, 'Will the issue and expiry dates of my existing permit be subject to change?', 'No. An updated permit only reflects changes to classification type or number of rooms. The original permit issue and expiry dates remain the same.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (408, 'Will additional fees be applied if I am adding rooms?', 'Yes. A standard administration fee is applicable to all online updates. Additional fees will also be incurred, based on the number of rooms being added, plus any outstanding Tourism Dirham charges.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (409, 'Can I amend the name of my building?', 'No. Building details cannot be updated here. Please contact the eServices support team for further assistance.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (410, 'How long will it take to update my permit?', 'Updates are applied as soon as payment is processed. The new permit can then be printed immediately.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (411, 'What documents are required to add a new user?', 'Up-to-date commercial licence copy for professional operators and passport or Emirates ID copy for professional operator''s designated manager or admin personnel.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (412, 'What is the validity period for a new user', 'A user is valid until deactivated in the system.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (413, 'How long before a new user is activated?', 'Within 24 hours.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (414, 'How many managers can be listed as holiday homes operators?', 'You need at least one manager but can have several. However, they must be specified as such on your trade licence.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (415, 'Is it possible to amend the unit owner on my permit?', 'No. The only amendments that can be made to a permit are to the classification type or number of rooms in the unit.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (416, 'I have a new manager on the trade licence how can I update the system?', 'Add a new manager by following the steps outlined above. Once they are approved and activated as a user you must deactivate the previous manager.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (417, 'What is the difference between an admin and manager user?', 'A manager has full system access and can add, remove and update properties. An admin user can only check in and check out guests.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (418, 'I have a technical issue.', 'Contact our expert eServices support team who will be happy to help resolve any issues.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (419, 'How long does the classification process take?', 'You will be contacted and visited by the DET inspection team within one business day of submitting your application after which classification will be confirmed or rejected.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (420, 'How long does the renewal process take?', 'The DET inspection team will visit your property within 24 hours of application submission (if required). Your renewed classification certificate will be released within the same timeframe if the property passes the inspection.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (421, 'If I do not meet the hotel classification requirements then can I request a grace period?', 'In certain circumstances, a grace period can be granted. This will be assessed on a case-by-case basis.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (422, 'What is the hotel classification self-assessment?', 'The self-assessment does not need to be submitted as supporting documentation. It is a useful checklist that the property should use to evaluate suitability against DET classification criteria and minimum requirements.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (423, 'When can I start hotel operations after classification?', 'After obtaining the classification rating, you must apply for a hotel Tourism Licence from DET. The next step is to register with DET for the Tourism Dirham programme after which you can file a ''receive guests'' application to commence operations through the classifications portal.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (424, 'When do I need to renew my classification certificate?', 'Three months prior to the expiry date.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (425, 'Where can I find my renewed classification certificate?', 'Your printable certificate will be available online within your classification portal profile.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (426, 'Why has my hotel classification been rejected post inspection?', 'The property has not met the requirements of the requested classification type as detailed in the post-inspection report. The report identifies relevant gaps to be addressed prior to re-inspection.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (427, 'How long before I get my amended classification?', 'You will be visited by the DET inspection team within 24 hours of submitting your request after which the amended classification will be confirmed.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (428, 'How long before I get my NOC?', 'You will be contacted and visited by the DET inspection team within one business day of submitting your application after which classification will be confirmed or rejected.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (429, 'How long is the approval letter/NOC valid for?', 'The NOC is valid for six months, during which time it should be used to obtain the required building permits. If not used within this time, the letter will need to be re-issued.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (430, 'When should I apply for the construction NOC?', 'This is the initial step when constructing a new property or converting an existing building into a hotel and changing land use type. NOC permission must be granted before work can commence.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (431, 'Is there any available advisory support?', 'DET offers an advisory service that assists properties in meeting the criteria for requested classifications. It also identifies gaps and areas of improvement.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (432, 'If I apply for a Tourism Licence, will this initiate the classification process?', 'No. For a hotel establishment to be classified it must first register through the classifications portal. Once registered, the Tourism Licence number or Licence Application Number must be provided as the next step in applying for property classification.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (433, 'Whose passport and Emirates ID should be entered in the system?', 'We require the passport and Emirates ID of the selected administrator, supported by a Letter of Authority from the hotel owner or investor, giving them permission to access and use the Classifications portal.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (434, 'What if I have a multiple properties on the same transaction ID?', 'Each establishment/property needs to be registered individually and have a separate profile created, even if occupying the same plot. Registration is required per property, not per owner.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (435, 'What is a Letter of Authority?', 'The Letter of Authority gives the selected administrator permission to access and use the classifications portal on behalf of an owner.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (436, 'What is my Transaction ID?', 'Your Transaction ID is is the initial approval number for your project. Approvals are a prerequisite to project commencement and required for Classifications portal registration.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (437, 'How does the hotel advisory service work?', 'You can either schedule an onsite meeting or meet the team at our offices. Please request an appointment through the Classification portal.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (438, 'What are the fees to get hotel advisory services?', 'There is a fee of AED1,000 per hour exclusive of government innovation and knowledge fees.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (439, 'Why should I use the hotel advisory service?', 'The DET advisory consultancy was established to support our hospitality partners and owners and to ensure that your property specifications meet the necessary requirements throughout the build process and prior to opening.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (440, 'Will I receive a post-meeting advisory report?', 'Yes. Following our meeting an advisory report will be uploaded to to your profile on the classification portal.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (441, 'I am making changes to my room inventory. What documents do I need?', 'You will need an official signed and stamped company letter on official letterhead detailing the changes you wish to make.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (442, 'If I want to make rooms inoperable will it change my classification?', 'No. Closing rooms will not affect your property classification.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (443, 'If I permanently remove rooms from the inventory, how will this affect the renewal fees?', 'Annual renewal is based on the total number of units so the removal of rooms will be reflected in your renewal fees.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (444, 'Can I make new rooms available for guest bookings once my inventory change request has been approved?', 'No. For all newly added rooms you need to apply for a ''Receive New Guests'' NOC and await the successful conclusion of a further inspection before they can be open for use.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (445, 'Can I receive hotel guest bookings before I have received the no objection certificate?', 'You cannot operate without receiving an official no objection certificate from DET, so you will not be able to accept guest bookings.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (446, 'Why has my Receive Guests NOC been rejected post classification inspection?', 'The property has not met the requirements to receive guests. The DET inspection team will provide a post-inspection report identifying relevant gaps to be addressed prior to re-inspection.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (447, 'What happens after I register with Medyaf?', 'Once registered, you may book you a half hour Medyaf counselling session where we will help you understand in details all our services like career counselling, skills development training, volunteering, internships, summer/winter break student camps and Emirati Tour Guiding, and how they will help you enter the Tourism industry.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (448, 'Do you help students or graduates?', 'Both. Medyaf supports all students who are interested in working in pursuing a career in the tourism industry with summer and winter school-break training programmes, industry internships, volunteering opportunities. Medyaf supports graduates through hiring opportunities both full time and part time with tourism industry partners.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (449, 'How can I become an Emirati Tour Guide?', 'The free UAE National Tour Guide training programme is a nine-day course, please visit www.tourguidetraining.ae to register.', 1, null, '2022-10-21 21:59:09', '2022-10-21 21:59:09');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (450, 'Can I run a sales promotion as part of a Dubai festival?', 'Yes, entities that plan to hold a promotion can submit their application together with all the required details, documents and fees by following the above steps to receive a permit to run a promotion.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (451, 'What types of promotions can I run during a festival?', 'Promotion types allowed include price deals (for example, sales, discounts, rebates, clearance, etc.), special offers (for example, bulk bundles or gift with purchase, etc.) raffles, contests, pop-up kiosks, booths and stands.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (452, 'What is the validity period for a promotion certificate of participation?', 'Once you pay the Retail Calendar Participation Fee for the calendar year your permit is valid for 12 months and covers specified seasonal retail windows such as Dubai Shopping Festival, Dubai Summer Surprises, Dubai Food Festival, etc. We also offer off-season calendar participation with a different set of fees and guidelines, where retail partners can also run promotions.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (453, 'Can I apply for multiple promotion certificates or multiple festivals at the same time?', 'Yes. You may select the festivals you would like to run promotions and submit the promotion details for each festival respectively.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (454, 'What channels are there to pay promotion certificate fees?', 'The payment of the calendar promotion permit fee must be made before the submission of the promotion details. Fees can be either paid online through internet banking, credit card or at an Al Ansari Exchange branch via cash or cheque.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (455, 'Can I make amendments to an issued promotion certificate?', 'You can make amendments to your promotion application after you receive the permit by sending a request to retailcalendar@dubaitourism.ae', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (456, 'I wish to participate, but won’t be able to attend the full event duration. Can I still apply?', 'Yes. There are some limited spaces that will cater to shorter participation. Please contact us with your requirements.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (457, 'Do you offer support for the design and branding of my event structure?', 'Once your participation has been approved, support services are available for a fee. Contact your curation team with your request.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (458, 'Can I share my event space with other vendors?', 'Food and beverage vendors cannot share their event space. Retail vendors may be able to share event space. Contact your curation team with your request.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (459, 'I do not have enough staff to help with my event. Do you provide any staff to support with my event?', 'Staffing assistance is not provided. This is the responsibility of the participant.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (460, 'Can I run my own signature workshop or activation?', 'Yes. We actively encourage this. Please contact us with your ideas, once your participation has been approved.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (461, 'What health and safety requirements do I need to meet to run my event?', 'For Modesh World, any funfair operators who wish to participate are required to have all necessary health and safety certification in place. Additionally, all staff must be fully certified and qualified.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (462, 'How far in advance can apply to participate in a DET-run event?', 'We encourage all vendors to apply a minimum of two months prior to the event.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (463, 'Where is the safari driver training course held?', 'Training is held at Dubai College of Tourism, at DET Headquarters: One central building 2, floor 4. If an alternative venue is needed, then attendees will be notified prior to the course commencement.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (464, 'What languages is the safari driver training conducted in?', 'The course is English because the ability to effectively communicate with international visitors is very important to your role. One of the requirements when applying for the course is proof of English language proficiency.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (465, 'Where can I find the latest desert safari materials to help me study for the exam?', 'The tour guide platform contains all the latest materials. You can access this after paying the course fee.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (466, 'How can I improve my chances of passing the safari driver training?', 'Being a safari driver needs vehicle and driving skills. Even more importantly, it requires the ability to communicate knowledgeably and confidently with your guests. So, practice your social skills and language competency. You can find helpful materials to help you study for the exam on the safari training platform.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (467, 'What if I fail the retake exam?', 'If you do not pass this assessment you are allowed a retake. The retake fee is AED120 (AED100 + AED10  knowledge fee + AED10 innovation fee).', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (468, 'How long is the safari driver training valid for?', 'The safari driver training is valid for one year, after which you will need to renew it. You can apply for a Safari Driver''s License with RTA throughout the validity of your training certificate.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (469, 'Where can I get a copy of my safari driver training certificate?', 'You can print your certificate from the tour guide desert safari system.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (470, 'Can I start driving safari vehicles as soon as I pass safari driver training?', 'No. You must first receive your Safari Driver''s License. This license is issued by the Dubai Roads & Transport Authority (RTA), not DCT who conducts your training. After you pass your safari driver training, DCT will update you traffic file directly on RTA. You can then apply for Safari Drivers License with RTA directly before driving any safari vehicles.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (471, 'Can I use a safari driver license issued in another emirate?', 'Yes, you can. Terms and conditions and fees apply', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (472, 'If I get my Safari Driver License, do I automatically get a Tour Guide License too?', 'No. The tour guide qualification is separate, you can apply for the training on the tour guide training portal. Passing the safari guide driver''s training is a mandatory requirement before you are able to apply for a Safari Driving License from the Roads and Transport Authority (RTA).', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (473, 'How much dose it cost to renew my safari driver training certificate?', 'The renewal course and exam cost AED520 including fees and tax.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (474, 'Can I claim for safari driver course fee refund or get the credit transferred to another course?', 'Course fee payment is not transferable or refundable.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (475, 'What documents are needed for a safari vehicle registration?', 'To apply for the safari vehicle registration, you must have the following: - A minimum of five registered drivers  - A minimum of two drivers holding first aid certificate - A minimum of five vehicles owned by the company and with GPS devices installed - Comprehensive company insurance', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (476, 'How long is a safari vehicle registration valid for?', 'The safari vehicle registration is valid for a year or until the expiry of the company''s trade license, whichever comes first.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (477, 'Can change the details of my safari vehicle registration?', 'You can only add or remove drivers and/or vehicles.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (478, 'Will my safari vehicle registration be valid if my RTA vehicle registration expires?', 'The vehicle safari registration will not be valid if the vehicle RTA registration expires. You must re-register your vehicle with RTA and update the safari registration for it to be valid.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (479, 'How can I renew my safari vehicle registration?', 'If your vehicle safari registration has expired you have to apply for a new one.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (480, 'Can I make changes to my vehicle registration at any time?', 'Amends can be made up to 24 hours before a the registration is due to expire.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (481, 'My safari vehicle registration will soon, can I still amend it and add more vehicles?', 'Yes, you can. But when your safari registration expires so will all registered vehicles, including the newly added vehicles. You will need to make a new safari vehicle registration and add all vehicles again.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (482, 'Can I hire freelancers with their vehicles and add them to my existing safari registration?', 'Yes, at any time you can add freelance drivers and vehicles to a valid safari registration.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (483, 'What is iSupplier and what is used for?', 'iSupplier is a web based online vendor management system that allows suppliers register and receive enquiries such as RFQs, RFIs, RFTs & RFPs. The portal dashboard allows direct communication between suppliers, DET Procurement and business units, while maintaining the full communication history within the portal. The portal e-Sourcing & e-Invoice Management Systems allow to release and maintain records of all purchase orders and invoices by suppliers. Suppliers can submit and track their invoices online and be paid through an online transaction. To keep suppliers updated on the progress of their services all portal features are supported by integrated email push notifications.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (484, 'How do I receive enquiries?', 'Suppliers receive notifications via email for new enquiries such as RFQs, RFIs, RFTs and RFPs. A supplier can access the detailed information online via the portal.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (485, 'How do I reset my username or password?', 'Suppliers can reset their password from the iSupplier login screen, or by contacting the procurement team directly. Contact us here.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (486, 'How can I update my company name on the portal?', 'Suppliers must submit an official letter on headed paper, outlining the new name of the company. DET will make the changes on the platform directly.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (487, 'Can my company have multiple users under one account', 'Yes. Each company account has a main primary user account. The primary user account can then create additional user accounts.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (488, 'Who is eligible to register on the iSupplier portal?', 'All suppliers holding a valid commercial document or license can create an account on iSupplier.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (489, 'Can companies from other emirates or other countries register on iSupplier?', 'Yes, any company holding a valid commercial license can register.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (490, 'What are the documents needed to register as a supplier?', 'Suppliers must submit the following documents as attachments when completing the registration process: Trade/Commercial License document Certificate of Incorporation Company Stamp Sample on blank background Bank Details in a separate Letter', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (491, 'What if I am an overseas company and I do not have the required information and cannot submit Passport and copies of personal IDs', 'If you cannot provide the required documents or information on behalf of your company or individual users, you must submit the company letter stating authorised signatory Name and Signature Sample.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (492, 'What is the bank letter and what is the information required on it', 'A bank letter outlines the information of the beneficiary account of the company being registered. It should contain the following information; Bank Name, Beneficiary Name, Account Number, IBAN Number, Swift Code and Address.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (493, 'Can I register in person or by mail?', 'No, the registration process must be done online. If you are experiencing problems or need clarification, please contact us and we’ll be glad to help.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (494, 'Does an iSupplier account expire?', 'Yes, an account will be deactivated if the provided trade or commercial license expires. All suppliers will receive an email notification 30 days prior to the expiry of their registered documents. Suppliers should update the documents submitted on the portal before they expire expiration to avoid their account deactivated.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (495, 'What happens if I do not renew my documents and my account is deactivated?', 'The account will be deactivated and will not be able to participate in new RFPs & RFQs. You will need to contact us and provide all documentation again in order to reactivate your account.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (496, 'Is it possible to submit invoices in person?', 'No, all invoices must be submitted through portal.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (497, 'When should I submit an invoice?', 'Once the service or work is delivered to the business unit, the client will request an invoice via the portal and the supplier will receive an email notification to submit their invoice via iSupplier.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (498, 'What documents are required to submit an invoice?', 'To submit an invoice, suppliers are required to submit a digital copy of the invoice and a signed delivery note. If you cannot provide a delivery note, you can also submit attendance sheets, a service report, certificate of completion or an email acknowledgement from DET.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (499, 'How do I follow up on invoice payments?', 'To check a payment status or follow up on a payment, please log in to the portal account and search the status via Invoice Catalog.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (500, 'Why was my invoice rejected?', 'You can find details of an invoice including comments and reasons for rejection by selecting the invoice from the Invoice Catalog, and then clicking on invoice/payment summary details.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (501, 'What payment methods are used by DET?', 'DET makes payments via online bank transfers and bank cheques.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (502, 'How long does it take to complete the English tour guide course?', 'This depends on the individual, but the average length is 4-6 weeks.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (503, 'How long is the Mandarin tour guide course?', 'The Mandarin course last 10 days.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (504, 'How can I differentiate myself to pass the Tour Guide test?', 'By practicing your presentation skills, and gaining insight into Dubai’s attractions, architectural and cultural influences. There are also great opportunities for multi-lingual candidates. Particularly those who have fluency in Japanese, Korean, Brazilian, Portuguese or Russian as well as English or Mandarin.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (505, 'What tour guiding modules will I need to study?', 'Applicants will study a variety of modules including: Dubai Way, Inclusive Service, Tour Guide Essentials, Advanced Tour Guiding Skills, The Expert Tour Guide and Dealing with Difficult Situations.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (506, 'What languages are in demand for tour guides right now?', 'We welcome all candidates with language skills with a particularly requirement for those who can also speak Japanese, Korean, Portugese and Russian in addition to either English or Mandarin.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (507, 'Can I pay for a tour guide course in instalments?', 'You cannot pay in installments, but the course is structured into different stages and you pay when you are ready to attend each stage.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (508, 'What happens if my license expires?', 'You cannot guide without a valid tour guide license, even if you already applied to renew it. If you continue guiding after your license expires, you will face a fine. If you do not wish to use you license anymore, you must cancel it to avoid a fine.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (509, 'How quickly do I have to pay a fine if my license expired?', 'If you continue guiding after your license has expired, you will face a fine. The fine is AED200 for the first two years after expiration, then it increases to AED400 for up to 4 years. Failure to pay the fine within the four years will result in the cancellation of your license. To avoid a fine, you must cancel your license.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (510, 'I lost my license, can I get a new one?', 'Obtain a certificate of loss item from Dubai police, upload the police report and copy of the lost license to your account on the website and we will verify and dispatch a new one to you.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (511, 'What upcoming trade shows is DET participating in?', 'You can find a full calendar of events on our trade website.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (512, 'Are services like catering and Wi-Fi included in the cost of participating?', 'Catering is provided on the stand. Wi-Fi is reviewed on a case-by-case basis.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (513, 'Do you provide any marketing or promotional support if we join DTCM at events?', 'Marketing or promotional support is not provided.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (514, 'How many participants are included in each application for a trade event?', 'The number of participants is different from one event to another. Our team will advise you once your registration is completed.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (515, 'Do you provide any support in obtaining visas?', 'We provide registered exhibitors with a Letter of Invitation and – where applicable – a copy of their event badge.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
INSERT INTO railway.questions (id, text, answer, language_id, keywords_extracted_at, created_at, updated_at) VALUES (516, 'What support do you provide if I have any special requests?', 'We are happy to assist with any questions or queries. Please contact us with details of your request and we will see how we can help.', 1, null, '2022-10-21 21:59:10', '2022-10-21 21:59:10');
